import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import Loading from '../components/Loading';
import './Profile.css';

const Profile = () => {
  const { user, updateUser } = useAuth();
  const [loading, setLoading] = useState(false);
  const [editing, setEditing] = useState(false);
  const [activeTab, setActiveTab] = useState('profile');
  const [profileData, setProfileData] = useState({
    name: '',
    email: '',
    phoneNumber: '',
    dateOfBirth: '',
    address: '',
    bio: '',
    avatar: null
  });
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [stats, setStats] = useState({
    totalExams: 0,
    completedExams: 0,
    averageScore: 0,
    totalStudents: 0,
    createdExams: 0
  });
  const [achievements, setAchievements] = useState([]);

  useEffect(() => {
    if (user) {
      setProfileData({
        name: user.name || '',
        email: user.email || '',
        phoneNumber: user.phoneNumber || '',
        dateOfBirth: user.dateOfBirth ? user.dateOfBirth.split('T')[0] : '',
        address: user.address || '',
        bio: user.bio || '',
        avatar: user.avatar
      });
      fetchUserStats();
      fetchUserAchievements();
    }
  }, [user]);

  const fetchUserStats = async () => {
    try {
      const response = await api.get('/users/stats');
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching user stats:', error);
    }
  };

  const fetchUserAchievements = async () => {
    try {
      const response = await api.get('/users/achievements');
      setAchievements(response.data);
    } catch (error) {
      console.error('Error fetching achievements:', error);
    }
  };

  const handleProfileChange = (field, value) => {
    setProfileData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handlePasswordChange = (field, value) => {
    setPasswordData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setProfileData(prev => ({
          ...prev,
          avatar: e.target.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const formData = new FormData();
      Object.keys(profileData).forEach(key => {
        if (key === 'avatar' && profileData[key] instanceof File) {
          formData.append('avatar', profileData[key]);
        } else if (key !== 'avatar') {
          formData.append(key, profileData[key]);
        }
      });

      const response = await api.put('/users/profile', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      updateUser(response.data.user);
      setEditing(false);
      alert('Profile updated successfully!');
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Error updating profile. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      alert('New passwords do not match');
      return;
    }

    if (passwordData.newPassword.length < 6) {
      alert('Password must be at least 6 characters long');
      return;
    }

    setLoading(true);

    try {
      await api.put('/users/password', {
        currentPassword: passwordData.currentPassword,
        newPassword: passwordData.newPassword
      });

      setPasswordData({
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
      });
      alert('Password updated successfully!');
    } catch (error) {
      console.error('Error updating password:', error);
      alert(error.response?.data?.message || 'Error updating password');
    } finally {
      setLoading(false);
    }
  };

  const getRoleStats = () => {
    switch (user?.role) {
      case 'student':
        return [
          { label: 'Exams Taken', value: stats.totalExams, icon: 'fas fa-clipboard-list' },
          { label: 'Completed', value: stats.completedExams, icon: 'fas fa-check-circle' },
          { label: 'Average Score', value: `${stats.averageScore}%`, icon: 'fas fa-chart-line' },
          { label: 'Rank', value: '#42', icon: 'fas fa-trophy' }
        ];
      case 'teacher':
        return [
          { label: 'Exams Created', value: stats.createdExams, icon: 'fas fa-plus-circle' },
          { label: 'Total Students', value: stats.totalStudents, icon: 'fas fa-users' },
          { label: 'Active Exams', value: stats.activeExams, icon: 'fas fa-play-circle' },
          { label: 'Success Rate', value: `${stats.successRate}%`, icon: 'fas fa-chart-bar' }
        ];
      case 'parent':
        return [
          { label: 'Children', value: stats.totalChildren, icon: 'fas fa-child' },
          { label: 'Notifications', value: stats.notifications, icon: 'fas fa-bell' },
          { label: 'Reports Viewed', value: stats.reportsViewed, icon: 'fas fa-file-alt' },
          { label: 'Meetings', value: stats.meetings, icon: 'fas fa-handshake' }
        ];
      default:
        return [];
    }
  };

  if (loading) return <Loading message="Updating profile..." overlay />;

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-avatar">
          <img 
            src={profileData.avatar || '/default-avatar.png'} 
            alt={user?.name}
            className="avatar-image"
          />
          <div className="avatar-overlay">
            <i className="fas fa-camera"></i>
          </div>
        </div>
        <div className="profile-info">
          <h1>{user?.name}</h1>
          <p className="profile-role">{user?.role?.toUpperCase()}</p>
          <p className="profile-email">{user?.email}</p>
          <div className="profile-stats">
            <span>Member since {new Date(user?.createdAt).getFullYear()}</span>
            <span>Last login: {new Date(user?.lastLogin).toLocaleDateString()}</span>
          </div>
        </div>
        <div className="profile-actions">
          <button 
            className="btn btn-primary"
            onClick={() => setEditing(!editing)}
          >
            <i className="fas fa-edit"></i>
            {editing ? 'Cancel' : 'Edit Profile'}
          </button>
        </div>
      </div>

      <div className="profile-content">
        <div className="profile-sidebar">
          <div className="profile-nav">
            <button 
              className={`nav-item ${activeTab === 'profile' ? 'active' : ''}`}
              onClick={() => setActiveTab('profile')}
            >
              <i className="fas fa-user"></i>
              Profile
            </button>
            <button 
              className={`nav-item ${activeTab === 'stats' ? 'active' : ''}`}
              onClick={() => setActiveTab('stats')}
            >
              <i className="fas fa-chart-bar"></i>
              Statistics
            </button>
            <button 
              className={`nav-item ${activeTab === 'achievements' ? 'active' : ''}`}
              onClick={() => setActiveTab('achievements')}
            >
              <i className="fas fa-trophy"></i>
              Achievements
            </button>
            <button 
              className={`nav-item ${activeTab === 'security' ? 'active' : ''}`}
              onClick={() => setActiveTab('security')}
            >
              <i className="fas fa-shield-alt"></i>
              Security
            </button>
          </div>
        </div>

        <div className="profile-main">
          {activeTab === 'profile' && (
            <div className="tab-content">
              <div className="section-header">
                <h2>Profile Information</h2>
                <p>Manage your personal information and preferences</p>
              </div>

              <form onSubmit={handleProfileSubmit} className="profile-form">
                <div className="form-section">
                  <h3>Personal Information</h3>
                  <div className="form-grid">
                    <div className="form-group">
                      <label>Full Name</label>
                      <input
                        type="text"
                        value={profileData.name}
                        onChange={(e) => handleProfileChange('name', e.target.value)}
                        disabled={!editing}
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label>Email</label>
                      <input
                        type="email"
                        value={profileData.email}
                        onChange={(e) => handleProfileChange('email', e.target.value)}
                        disabled={!editing}
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label>Phone Number</label>
                      <input
                        type="tel"
                        value={profileData.phoneNumber}
                        onChange={(e) => handleProfileChange('phoneNumber', e.target.value)}
                        disabled={!editing}
                      />
                    </div>
                    
                    <div className="form-group">
                      <label>Date of Birth</label>
                      <input
                        type="date"
                        value={profileData.dateOfBirth}
                        onChange={(e) => handleProfileChange('dateOfBirth', e.target.value)}
                        disabled={!editing}
                      />
                    </div>
                  </div>
                  
                  <div className="form-group">
                    <label>Address</label>
                    <textarea
                      value={profileData.address}
                      onChange={(e) => handleProfileChange('address', e.target.value)}
                      disabled={!editing}
                      rows="3"
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>Bio</label>
                    <textarea
                      value={profileData.bio}
                      onChange={(e) => handleProfileChange('bio', e.target.value)}
                      disabled={!editing}
                      rows="4"
                      placeholder="Tell us about yourself..."
                    />
                  </div>
                  
                  {editing && (
                    <div className="form-group">
                      <label>Profile Picture</label>
                      <input
                        type="file"
                        accept="image/*"
                        onChange={handleAvatarChange}
                        className="file-input"
                      />
                    </div>
                  )}
                </div>

                {editing && (
                  <div className="form-actions">
                    <button type="submit" className="btn btn-primary">
                      <i className="fas fa-save"></i>
                      Save Changes
                    </button>
                    <button 
                      type="button" 
                      className="btn btn-secondary"
                      onClick={() => setEditing(false)}
                    >
                      Cancel
                    </button>
                  </div>
                )}
              </form>
            </div>
          )}

          {activeTab === 'stats' && (
            <div className="tab-content">
              <div className="section-header">
                <h2>Statistics</h2>
                <p>Your performance and activity overview</p>
              </div>

              <div className="stats-grid">
                {getRoleStats().map((stat, index) => (
                  <div key={index} className="stat-card">
                    <div className="stat-icon">
                      <i className={stat.icon}></i>
                    </div>
                    <div className="stat-info">
                      <h3>{stat.value}</h3>
                      <p>{stat.label}</p>
                    </div>
                  </div>
                ))}
              </div>

              {user?.role === 'student' && (
                <div className="performance-chart">
                  <h3>Performance Over Time</h3>
                  <div className="chart-placeholder">
                    <p>Chart visualization would be implemented here</p>
                  </div>
                </div>
              )}
            </div>
          )}

          {activeTab === 'achievements' && (
            <div className="tab-content">
              <div className="section-header">
                <h2>Achievements</h2>
                <p>Your accomplishments and milestones</p>
              </div>

              <div className="achievements-grid">
                {achievements.length > 0 ? (
                  achievements.map((achievement, index) => (
                    <div key={index} className="achievement-card">
                      <div className="achievement-icon">
                        <i className={achievement.icon}></i>
                      </div>
                      <div className="achievement-info">
                        <h4>{achievement.title}</h4>
                        <p>{achievement.description}</p>
                        <span className="achievement-date">
                          {new Date(achievement.earnedAt).toLocaleDateString()}
                        </span>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="no-achievements">
                    <i className="fas fa-trophy"></i>
                    <p>No achievements yet. Keep working to unlock them!</p>
                  </div>
                )}
              </div>
            </div>
          )}

          {activeTab === 'security' && (
            <div className="tab-content">
              <div className="section-header">
                <h2>Security Settings</h2>
                <p>Manage your account security and password</p>
              </div>

              <form onSubmit={handlePasswordSubmit} className="security-form">
                <div className="form-section">
                  <h3>Change Password</h3>
                  <div className="form-group">
                    <label>Current Password</label>
                    <input
                      type="password"
                      value={passwordData.currentPassword}
                      onChange={(e) => handlePasswordChange('currentPassword', e.target.value)}
                      required
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>New Password</label>
                    <input
                      type="password"
                      value={passwordData.newPassword}
                      onChange={(e) => handlePasswordChange('newPassword', e.target.value)}
                      required
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>Confirm New Password</label>
                    <input
                      type="password"
                      value={passwordData.confirmPassword}
                      onChange={(e) => handlePasswordChange('confirmPassword', e.target.value)}
                      required
                    />
                  </div>
                  
                  <button type="submit" className="btn btn-primary">
                    <i className="fas fa-key"></i>
                    Update Password
                  </button>
                </div>
              </form>

              <div className="security-info">
                <h3>Security Information</h3>
                <div className="security-item">
                  <i className="fas fa-shield-alt"></i>
                  <div>
                    <h4>Account Security</h4>
                    <p>Your account is protected with industry-standard security measures</p>
                  </div>
                </div>
                <div className="security-item">
                  <i className="fas fa-clock"></i>
                  <div>
                    <h4>Last Login</h4>
                    <p>{new Date(user?.lastLogin).toLocaleString()}</p>
                  </div>
                </div>
                <div className="security-item">
                  <i className="fas fa-mobile-alt"></i>
                  <div>
                    <h4>Two-Factor Authentication</h4>
                    <p>Enable 2FA for enhanced security (Coming Soon)</p>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
