// frontend/src/pages/StudentDashboard.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import StatsCard from '../components/StatsCard';
import ExamCard from '../components/ExamCard';
import { toast } from 'react-toastify';
import './StudentDashboard.css';

const StudentDashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState({});
  const [upcomingExams, setUpcomingExams] = useState([]);
  const [recentResults, setRecentResults] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const response = await api.get('/student/dashboard');
      setStats(response.data.stats);
      setUpcomingExams(response.data.upcomingExams);
      setRecentResults(response.data.recentResults);
      setNotifications(response.data.notifications);
    } catch (error) {
      toast.error('Failed to fetch dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const getGradeColor = (percentage) => {
    if (percentage >= 90) return '#28a745'; // Green
    if (percentage >= 80) return '#17a2b8'; // Blue
    if (percentage >= 70) return '#ffc107'; // Yellow
    if (percentage >= 60) return '#fd7e14'; // Orange
    return '#dc3545'; // Red
  };

  const getGradeLetter = (percentage) => {
    if (percentage >= 90) return 'A';
    if (percentage >= 80) return 'B';
    if (percentage >= 70) return 'C';
    if (percentage >= 60) return 'D';
    return 'F';
  };

  if (loading) {
    return <div className="loading">Loading student dashboard...</div>;
  }

  return (
    <div className="student-dashboard">
      <div className="dashboard-header">
        <div className="welcome-section">
          <h1>Welcome back, {user?.name}!</h1>
          <p>Ready to take your next exam?</p>
        </div>
        <div className="quick-stats">
          <div className="quick-stat">
            <span className="stat-number">{upcomingExams.length}</span>
            <span className="stat-label">Upcoming Exams</span>
          </div>
          <div className="quick-stat">
            <span className="stat-number">{recentResults.length}</span>
            <span className="stat-label">Recent Results</span>
          </div>
        </div>
      </div>

      <div className="stats-grid">
        <StatsCard
          title="Total Exams Taken"
          value={stats.totalExams || 0}
          icon="fas fa-clipboard-check"
          trend={stats.examsTrend}
        />
        <StatsCard
          title="Average Score"
          value={`${stats.averageScore || 0}%`}
          icon="fas fa-chart-line"
          trend={stats.scoreTrend}
        />
        <StatsCard
          title="Completed Exams"
          value={stats.completedExams || 0}
          icon="fas fa-check-circle"
          trend={stats.completedTrend}
        />
        <StatsCard
          title="Pending Results"
          value={stats.pendingResults || 0}
          icon="fas fa-hourglass-half"
          trend={stats.pendingTrend}
        />
      </div>

      <div className="dashboard-content">
        <div className="main-content">
          <div className="upcoming-exams">
            <div className="section-header">
              <h2>Upcoming Exams</h2>
              <Link to="/exam-list" className="view-all-link">
                View All
              </Link>
            </div>
            
            {upcomingExams.length === 0 ? (
              <div className="empty-state">
                <i className="fas fa-calendar-check"></i>
                <h3>No upcoming exams</h3>
                <p>Check back later for new exam schedules</p>
              </div>
            ) : (
              <div className="exams-grid">
                {upcomingExams.map(exam => (
                  <div key={exam._id} className="exam-card">
                    <div className="exam-header">
                      <h3>{exam.title}</h3>
                      <span className="exam-subject">{exam.subject}</span>
                    </div>
                    <div className="exam-details">
                      <div className="exam-time">
                        <i className="fas fa-clock"></i>
                        <span>{new Date(exam.startTime).toLocaleDateString()}</span>
                      </div>
                      <div className="exam-duration">
                        <i className="fas fa-stopwatch"></i>
                        <span>{exam.duration} minutes</span>
                      </div>
                      <div className="exam-questions">
                        <i className="fas fa-question-circle"></i>
                        <span>{exam.questions?.length || 0} questions</span>
                      </div>
                    </div>
                    <div className="exam-actions">
                      <Link 
                        to={`/exam/${exam._id}`}
                        className="btn-take-exam"
                      >
                        Take Exam
                      </Link>
                      <Link 
                        to={`/exam/${exam._id}/details`}
                        className="btn-view-details"
                      >
                        View Details
                      </Link>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          <div className="recent-results">
            <div className="section-header">
              <h2>Recent Results</h2>
              <Link to="/exam-result" className="view-all-link">
                View All
              </Link>
            </div>
            
            {recentResults.length === 0 ? (
              <div className="empty-state">
                <i className="fas fa-chart-bar"></i>
                <h3>No recent results</h3>
                <p>Your exam results will appear here</p>
              </div>
            ) : (
              <div className="results-list">
                {recentResults.map(result => (
                  <div key={result._id} className="result-card">
                    <div className="result-info">
                      <h3>{result.exam.title}</h3>
                      <p className="result-subject">{result.exam.subject}</p>
                      <p className="result-date">
                        Completed: {new Date(result.submittedAt).toLocaleDateString()}
                      </p>
                    </div>
                    <div className="result-score">
                      <div 
                        className="score-circle"
                        style={{ 
                          borderColor: getGradeColor((result.score / result.exam.totalMarks) * 100)
                        }}
                      >
                        <span className="score-percentage">
                          {Math.round((result.score / result.exam.totalMarks) * 100)}%
                        </span>
                        <span 
                          className="score-grade"
                          style={{ 
                            color: getGradeColor((result.score / result.exam.totalMarks) * 100)
                          }}
                        >
                          {getGradeLetter((result.score / result.exam.totalMarks) * 100)}
                        </span>
                      </div>
                    </div>
                    <div className="result-actions">
                      <Link 
                        to={`/exam-result/${result._id}`}
                        className="btn-view-result"
                      >
                        View Details
                      </Link>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        <div className="sidebar">
          <div className="notifications-widget">
            <h3>Recent Notifications</h3>
            {notifications.length === 0 ? (
              <div className="no-notifications">
                <i className="fas fa-bell-slash"></i>
                <p>No new notifications</p>
              </div>
            ) : (
              <div className="notifications-list">
                {notifications.slice(0, 5).map(notification => (
                  <div key={notification._id} className="notification-item">
                    <div className="notification-icon">
                      <i className={notification.icon || 'fas fa-info-circle'}></i>
                    </div>
                    <div className="notification-content">
                      <p>{notification.message}</p>
                      <span className="notification-time">
                        {new Date(notification.createdAt).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            )}
            <Link to="/notifications" className="view-all-notifications">
              View All Notifications
            </Link>
          </div>

          <div className="quick-actions-widget">
            <h3>Quick Actions</h3>
            <div className="quick-actions">
              <Link to="/exam-list" className="quick-action">
                <i className="fas fa-list"></i>
                <span>Browse Exams</span>
              </Link>
              <Link to="/exam-result" className="quick-action">
                <i className="fas fa-chart-line"></i>
                <span>View Results</span>
              </Link>
              <Link to="/profile" className="quick-action">
                <i className="fas fa-user"></i>
                <span>My Profile</span>
              </Link>
              <Link to="/help" className="quick-action">
                <i className="fas fa-question-circle"></i>
                <span>Get Help</span>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
