// frontend/src/pages/TeacherDashboard.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import StatsCard from '../components/StatsCard';
import ExamCard from '../components/ExamCard';
import SearchBar from '../components/SearchBar';
import Modal from '../components/Modal';
import { toast } from 'react-toastify';
import './TeacherDashboard.css';

const TeacherDashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState({});
  const [exams, setExams] = useState([]);
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [searchTerm, setSearchTerm] = useState('');
  const [showGradeModal, setShowGradeModal] = useState(false);
  const [selectedSubmission, setSelectedSubmission] = useState(null);
  const [grade, setGrade] = useState('');
  const [feedback, setFeedback] = useState('');

  useEffect(() => {
    fetchDashboardData();
  }, []);

  useEffect(() => {
    if (activeTab === 'submissions') {
      fetchSubmissions();
    }
  }, [activeTab, searchTerm]);

  const fetchDashboardData = async () => {
    try {
      const response = await api.get('/teacher/dashboard');
      setStats(response.data.stats);
      setExams(response.data.exams);
    } catch (error) {
      toast.error('Failed to fetch dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const fetchSubmissions = async () => {
    try {
      const response = await api.get('/teacher/submissions', {
        params: { search: searchTerm }
      });
      setSubmissions(response.data);
    } catch (error) {
      toast.error('Failed to fetch submissions');
    }
  };

  const handleGradeSubmission = async () => {
    try {
      await api.patch(`/submissions/${selectedSubmission._id}/grade`, {
        score: grade,
        feedback: feedback
      });
      toast.success('Submission graded successfully');
      setShowGradeModal(false);
      setSelectedSubmission(null);
      setGrade('');
      setFeedback('');
      fetchSubmissions();
    } catch (error) {
      toast.error('Failed to grade submission');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active':
        return '#28a745';
      case 'scheduled':
        return '#ffc107';
      case 'completed':
        return '#6c757d';
      default:
        return '#17a2b8';
    }
  };

  if (loading) {
    return <div className="loading">Loading teacher dashboard...</div>;
  }

  return (
    <div className="teacher-dashboard">
      <div className="dashboard-header">
        <h1>Teacher Dashboard</h1>
        <p>Welcome back, {user?.name}</p>
        <Link to="/create-exam" className="btn-create-exam">
          <i className="fas fa-plus"></i>
          Create New Exam
        </Link>
      </div>

      <div className="dashboard-tabs">
        <button 
          className={activeTab === 'overview' ? 'active' : ''}
          onClick={() => setActiveTab('overview')}
        >
          Overview
        </button>
        <button 
          className={activeTab === 'exams' ? 'active' : ''}
          onClick={() => setActiveTab('exams')}
        >
          My Exams
        </button>
        <button 
          className={activeTab === 'submissions' ? 'active' : ''}
          onClick={() => setActiveTab('submissions')}
        >
          Submissions
        </button>
        <button 
          className={activeTab === 'students' ? 'active' : ''}
          onClick={() => setActiveTab('students')}
        >
          Students
        </button>
      </div>

      {activeTab === 'overview' && (
        <div className="overview-tab">
          <div className="stats-grid">
            <StatsCard
              title="Total Exams"
              value={stats.totalExams || 0}
              icon="fas fa-clipboard-list"
              trend={stats.examsTrend}
            />
            <StatsCard
              title="Active Exams"
              value={stats.activeExams || 0}
              icon="fas fa-play-circle"
              trend={stats.activeExamsTrend}
            />
            <StatsCard
              title="Total Students"
              value={stats.totalStudents || 0}
              icon="fas fa-user-graduate"
              trend={stats.studentsTrend}
            />
            <StatsCard
              title="Pending Grades"
              value={stats.pendingGrades || 0}
              icon="fas fa-exclamation-triangle"
              trend={stats.pendingGradesTrend}
            />
          </div>

          <div className="recent-exams">
            <h3>Recent Exams</h3>
            <div className="exams-grid">
              {exams.slice(0, 6).map(exam => (
                <ExamCard
                  key={exam._id}
                  exam={exam}
                  showActions={true}
                  onEdit={() => {/* Handle edit */}}
                  onDelete={() => {/* Handle delete */}}
                />
              ))}
            </div>
          </div>

          <div className="quick-actions">
            <h3>Quick Actions</h3>
            <div className="actions-grid">
              <Link to="/create-exam" className="action-card">
                <i className="fas fa-plus-circle"></i>
                <h4>Create Exam</h4>
                <p>Set up a new exam for your students</p>
              </Link>
              <Link to="/exam-list" className="action-card">
                <i className="fas fa-list"></i>
                <h4>View All Exams</h4>
                <p>Manage all your created exams</p>
              </Link>
              <Link to="/notifications" className="action-card">
                <i className="fas fa-bell"></i>
                <h4>Notifications</h4>
                <p>Check exam notifications and alerts</p>
              </Link>
              <Link to="/settings" className="action-card">
                <i className="fas fa-cog"></i>
                <h4>Settings</h4>
                <p>Manage your account settings</p>
              </Link>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'exams' && (
        <div className="exams-tab">
          <div className="tab-header">
            <SearchBar
              onSearch={(term) => setSearchTerm(term)}
              placeholder="Search exams..."
            />
            <Link to="/create-exam" className="btn-primary">
              Create New Exam
            </Link>
          </div>

          <div className="exams-list">
            {exams.filter(exam => 
              exam.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
              exam.subject.toLowerCase().includes(searchTerm.toLowerCase())
            ).map(exam => (
              <div key={exam._id} className="exam-item">
                <div className="exam-info">
                  <h3>{exam.title}</h3>
                  <p className="exam-subject">{exam.subject}</p>
                  <p className="exam-date">
                    Start: {new Date(exam.startTime).toLocaleString()}
                  </p>
                  <p className="exam-duration">Duration: {exam.duration} minutes</p>
                </div>
                <div className="exam-stats">
                  <div className="stat">
                    <span className="stat-value">{exam.questions?.length || 0}</span>
                    <span className="stat-label">Questions</span>
                  </div>
                  <div className="stat">
                    <span className="stat-value">{exam.submissions?.length || 0}</span>
                    <span className="stat-label">Submissions</span>
                  </div>
                  <div className="stat">
                    <span 
                      className="stat-value status"
                      style={{ color: getStatusColor(exam.status) }}
                    >
                      {exam.status}
                    </span>
                    <span className="stat-label">Status</span>
                  </div>
                </div>
                <div className="exam-actions">
                  <Link to={`/exam/${exam._id}/edit`} className="btn-edit">
                    <i className="fas fa-edit"></i>
                    Edit
                  </Link>
                  <Link to={`/exam/${exam._id}/results`} className="btn-results">
                    <i className="fas fa-chart-bar"></i>
                    Results
                  </Link>
                  <button className="btn-delete">
                    <i className="fas fa-trash"></i>
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'submissions' && (
        <div className="submissions-tab">
          <SearchBar
            onSearch={(term) => setSearchTerm(term)}
            placeholder="Search submissions..."
          />

          <div className="submissions-table">
            <table>
              <thead>
                <tr>
                  <th>Student</th>
                  <th>Exam</th>
                  <th>Submitted</th>
                  <th>Score</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {submissions.map(submission => (
                  <tr key={submission._id}>
                    <td>{submission.student?.name}</td>
                    <td>{submission.exam?.title}</td>
                    <td>{new Date(submission.submittedAt).toLocaleDateString()}</td>
                    <td>
                      {submission.score !== null ? 
                        `${submission.score}/${submission.exam?.totalMarks}` : 
                        'Not graded'
                      }
                    </td>
                    <td>
                      <span className={`status-badge ${submission.status}`}>
                        {submission.status}
                      </span>
                    </td>
                    <td>
                      <div className="action-buttons">
                        <button
                          className="btn-view"
                          onClick={() => {
                            setSelectedSubmission(submission);
                            setShowGradeModal(true);
                          }}
                        >
                          Grade
                        </button>
                        <Link 
                          to={`/submission/${submission._id}`}
                          className="btn-details"
                        >
                          View
                        </Link>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* Grade Submission Modal */}
      <Modal
        isOpen={showGradeModal}
        onClose={() => setShowGradeModal(false)}
        title="Grade Submission"
      >
        {selectedSubmission && (
          <div className="grade-form">
            <div className="submission-info">
              <h3>{selectedSubmission.student?.name}</h3>
              <p>Exam: {selectedSubmission.exam?.title}</p>
              <p>Submitted: {new Date(selectedSubmission.submittedAt).toLocaleString()}</p>
            </div>
            
            <div className="form-group">
              <label>Score (out of {selectedSubmission.exam?.totalMarks})</label>
              <input
                type="number"
                value={grade}
                onChange={(e) => setGrade(e.target.value)}
                max={selectedSubmission.exam?.totalMarks}
                min="0"
                placeholder="Enter score"
              />
            </div>
            
            <div className="form-group">
              <label>Feedback (Optional)</label>
              <textarea
                value={feedback}
                onChange={(e) => setFeedback(e.target.value)}
                placeholder="Add feedback for the student"
                rows="4"
              />
            </div>
            
            <div className="modal-actions">
              <button 
                className="btn-cancel"
                onClick={() => setShowGradeModal(false)}
              >
                Cancel
              </button>
              <button 
                className="btn-submit"
                onClick={handleGradeSubmission}
              >
                Submit Grade
              </button>
            </div>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default TeacherDashboard;
