import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import Loading from '../components/Loading';
import './Settings.css';

const Settings = () => {
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('general');
  const [settings, setSettings] = useState({
    notifications: {
      emailNotifications: true,
      pushNotifications: true,
      examReminders: true,
      resultNotifications: true,
      systemUpdates: false
    },
    privacy: {
      profileVisibility: 'public',
      showEmail: false,
      showPhone: false,
      allowMessages: true
    },
    preferences: {
      language: 'en',
      timezone: 'UTC',
      theme: 'light',
      dateFormat: 'MM/DD/YYYY',
      timeFormat: '12h'
    },
    exam: {
      autoSave: true,
      confirmBeforeSubmit: true,
      showTimer: true,
      playSound: false,
      enableKeyboardShortcuts: true
    }
  });

  useEffect(() => {
    fetchSettings();
  }, []);

  const fetchSettings = async () => {
    try {
      const response = await api.get('/users/settings');
      setSettings(prev => ({
        ...prev,
        ...response.data
      }));
    } catch (error) {
      console.error('Error fetching settings:', error);
    }
  };

  const updateSetting = async (category, key, value) => {
    try {
      setSettings(prev => ({
        ...prev,
        [category]: {
          ...prev[category],
          [key]: value
        }
      }));

      await api.put('/users/settings', {
        category,
        key,
        value
      });
    } catch (error) {
      console.error('Error updating setting:', error);
    }
  };

  const saveAllSettings = async () => {
    setLoading(true);
    try {
      await api.put('/users/settings/bulk', settings);
      alert('Settings saved successfully!');
    } catch (error) {
      console.error('Error saving settings:', error);
      alert('Error saving settings. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const resetSettings = async () => {
    if (window.confirm('Are you sure you want to reset all settings to default?')) {
      setLoading(true);
      try {
        await api.post('/users/settings/reset');
        await fetchSettings();
        alert('Settings reset successfully!');
      } catch (error) {
        console.error('Error resetting settings:', error);
        alert('Error resetting settings. Please try again.');
      } finally {
        setLoading(false);
      }
    }
  };

  const exportData = async () => {
    try {
      const response = await api.get('/users/data/export');
      const blob = new Blob([JSON.stringify(response.data, null, 2)], {
        type: 'application/json'
      });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `user-data-${user.name.replace(/\s+/g, '-')}.json`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error exporting data:', error);
      alert('Error exporting data. Please try again.');
    }
  };

  const deleteAccount = async () => {
    const confirmText = prompt(
      'This action cannot be undone. Type "DELETE" to confirm account deletion:'
    );
    
    if (confirmText === 'DELETE') {
      try {
        await api.delete('/users/account');
        alert('Account deleted successfully. You will be logged out.');
        window.location.href = '/';
      } catch (error) {
        console.error('Error deleting account:', error);
        alert('Error deleting account. Please try again.');
      }
    }
  };

  if (loading) return <Loading message="Updating settings..." overlay />;

  return (
    <div className="settings-container">
      <div className="settings-header">
        <h1>Settings</h1>
        <p>Manage your account preferences and system settings</p>
      </div>

      <div className="settings-content">
        <div className="settings-sidebar">
          <nav className="settings-nav">
            <button 
              className={`nav-item ${activeTab === 'general' ? 'active' : ''}`}
              onClick={() => setActiveTab('general')}
            >
              <i className="fas fa-cog"></i>
              General
            </button>
            <button 
              className={`nav-item ${activeTab === 'notifications' ? 'active' : ''}`}
              onClick={() => setActiveTab('notifications')}
            >
              <i className="fas fa-bell"></i>
              Notifications
            </button>
            <button 
              className={`nav-item ${activeTab === 'privacy' ? 'active' : ''}`}
              onClick={() => setActiveTab('privacy')}
            >
              <i className="fas fa-shield-alt"></i>
              Privacy
            </button>
            <button 
              className={`nav-item ${activeTab === 'exam' ? 'active' : ''}`}
              onClick={() => setActiveTab('exam')}
            >
              <i className="fas fa-clipboard-list"></i>
              Exam Preferences
            </button>
            <button 
              className={`nav-item ${activeTab === 'data' ? 'active' : ''}`}
              onClick={() => setActiveTab('data')}
            >
              <i className="fas fa-database"></i>
              Data & Privacy
            </button>
          </nav>
        </div>

        <div className="settings-main">
          {activeTab === 'general' && (
            <div className="settings-section">
              <div className="section-header">
                <h2>General Preferences</h2>
                <p>Customize your general app preferences</p>
              </div>

              <div className="setting-group">
                <h3>Language & Region</h3>
                <div className="setting-item">
                  <label>Language</label>
                  <select
                    value={settings.preferences.language}
                    onChange={(e) => updateSetting('preferences', 'language', e.target.value)}
                  >
                    <option value="en">English</option>
                    <option value="es">Spanish</option>
                    <option value="fr">French</option>
                    <option value="de">German</option>
                    <option value="zh">Chinese</option>
                  </select>
                </div>
                
                <div className="setting-item">
                  <label>Timezone</label>
                  <select
                    value={settings.preferences.timezone}
                    onChange={(e) => updateSetting('preferences', 'timezone', e.target.value)}
                  >
                    <option value="UTC">UTC</option>
                    <option value="America/New_York">Eastern Time</option>
                    <option value="America/Chicago">Central Time</option>
                    <option value="America/Denver">Mountain Time</option>
                    <option value="America/Los_Angeles">Pacific Time</option>
                    <option value="Europe/London">London</option>
                    <option value="Europe/Paris">Paris</option>
                    <option value="Asia/Tokyo">Tokyo</option>
                  </select>
                </div>
              </div>

              <div className="setting-group">
                <h3>Display</h3>
                <div className="setting-item">
                  <label>Theme</label>
                  <select
                    value={settings.preferences.theme}
                    onChange={(e) => updateSetting('preferences', 'theme', e.target.value)}
                  >
                    <option value="light">Light</option>
                    <option value="dark">Dark</option>
                    <option value="auto">Auto</option>
                  </select>
                </div>
                
                <div className="setting-item">
                  <label>Date Format</label>
                  <select
                    value={settings.preferences.dateFormat}
                    onChange={(e) => updateSetting('preferences', 'dateFormat', e.target.value)}
                  >
                    <option value="MM/DD/YYYY">MM/DD/YYYY</option>
                    <option value="DD/MM/YYYY">DD/MM/YYYY</option>
                    <option value="YYYY-MM-DD">YYYY-MM-DD</option>
                  </select>
                </div>
                
                <div className="setting-item">
                  <label>Time Format</label>
                  <select
                    value={settings.preferences.timeFormat}
                    onChange={(e) => updateSetting('preferences', 'timeFormat', e.target.value)}
                  >
                    <option value="12h">12 Hour</option>
                    <option value="24h">24 Hour</option>
                  </select>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'notifications' && (
            <div className="settings-section">
              <div className="section-header">
                <h2>Notification Settings</h2>
                <p>Choose what notifications you want to receive</p>
              </div>

              <div className="setting-group">
                <h3>Email Notifications</h3>
                <div className="toggle-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={settings.notifications.emailNotifications}
                      onChange={(e) => updateSetting('notifications', 'emailNotifications', e.target.checked)}
                    />
                    <span className="toggle-slider"></span>
                    Enable email notifications
                  </label>
                  <p>Receive notifications via email</p>
                </div>
                
                <div className="toggle-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={settings.notifications.examReminders}
                      onChange={(e) => updateSetting('notifications', 'examReminders', e.target.checked)}
                    />
                    <span className="toggle-slider"></span>
                    Exam reminders
                  </label>
                  <p>Get reminded about upcoming exams</p>
                </div>
                
                <div className="toggle-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={settings.notifications.resultNotifications}
                      onChange={(e) => updateSetting('notifications', 'resultNotifications', e.target.checked)}
                    />
                    <span className="toggle-slider"></span>
                    Result notifications
                  </label>
                  <p>Get notified when exam results are available</p>
                </div>
              </div>

              <div className="setting-group">
                <h3>Push Notifications</h3>
                <div className="toggle-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={settings.notifications.pushNotifications}
                      onChange={(e) => updateSetting('notifications', 'pushNotifications', e.target.checked)}
                    />
                    <span className="toggle-slider"></span>
                    Browser notifications
                  </label>
                  <p>Receive push notifications in your browser</p>
                </div>
                
                <div className="toggle-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={settings.notifications.systemUpdates}
                      onChange={(e) => updateSetting('notifications', 'systemUpdates', e.target.checked)}
                    />
                    <span className="toggle-slider"></span>
                    System updates
                  </label>
                  <p>Get notified about system updates and maintenance</p>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'privacy' && (
            <div className="settings-section">
              <div className="section-header">
                <h2>Privacy Settings</h2>
                <p>Control your privacy and data sharing preferences</p>
              </div>

              <div className="setting-group">
                <h3>Profile Visibility</h3>
                <div className="setting-item">
                  <label>Profile visibility</label>
                  <select
                    value={settings.privacy.profileVisibility}
                    onChange={(e) => updateSetting('privacy', 'profileVisibility', e.target.value)}
                  >
                    <option value="public">Public</option>
                    <option value="private">Private</option>
                    <option value="contacts">Contacts only</option>
                  </select>
                  <p>Control who can see your profile information</p>
                </div>
                
                <div className="toggle-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={settings.privacy.showEmail}
                      onChange={(e) => updateSetting('privacy', 'showEmail', e.target.checked)}
                    />
                    <span className="toggle-slider"></span>
                    Show email address
                  </label>
                  <p>Allow others to see your email address</p>
                </div>
                
                <div className="toggle-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={settings.privacy.showPhone}
                      onChange={(e) => updateSetting('privacy', 'showPhone', e.target.checked)}
                    />
                    <span className="toggle-slider"></span>
                    Show phone number
                  </label>
                  <p>Allow others to see your phone number</p>
                </div>
              </div>

              <div className="setting-group">
                <h3>Communication</h3>
                <div className="toggle-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={settings.privacy.allowMessages}
                      onChange={(e) => updateSetting('privacy', 'allowMessages', e.target.checked)}
                    />
                    <span className="toggle-slider"></span>
                    Allow messages
                  </label>
                  <p>Allow other users to send you messages</p>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'exam' && (
            <div className="settings-section">
              <div className="section-header">
                <h2>Exam Preferences</h2>
                <p>Customize your exam-taking experience</p>
              </div>

              <div className="setting-group">
                <h3>Exam Interface</h3>
                <div className="toggle-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={settings.exam.autoSave}
                      onChange={(e) => updateSetting('exam', 'autoSave', e.target.checked)}
                    />
                    <span className="toggle-slider"></span>
                    Auto-save answers
                  </label>
                  <p>Automatically save your answers as you type</p>
                </div>
                
                <div className="toggle-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={settings.exam.confirmBeforeSubmit}
                      onChange={(e) => updateSetting('exam', 'confirmBeforeSubmit', e.target.checked)}
                    />
                    <span className="toggle-slider"></span>
                    Confirm before submit
                  </label>
                  <p>Show confirmation dialog before submitting exam</p>
                </div>
                
                <div className="toggle-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={settings.exam.showTimer}
                      onChange={(e) => updateSetting('exam', 'showTimer', e.target.checked)}
                    />
                    <span className="toggle-slider"></span>
                    Show timer
                  </label>
                  <p>Display remaining time during exam</p>
                </div>
              </div>

              <div className="setting-group">
                <h3>Accessibility</h3>
                <div className="toggle-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={settings.exam.playSound}
                      onChange={(e) => updateSetting('exam', 'playSound', e.target.checked)}
                    />
                    <span className="toggle-slider"></span>
                    Sound notifications
                  </label>
                  <p>Play sounds for important events during exam</p>
                </div>
                
                <div className="toggle-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={settings.exam.enableKeyboardShortcuts}
                      onChange={(e) => updateSetting('exam', 'enableKeyboardShortcuts', e.target.checked)}
                    />
                    <span className="toggle-slider"></span>
                    Keyboard shortcuts
                  </label>
                  <p>Enable keyboard shortcuts for faster navigation</p>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'data' && (
            <div className="settings-section">
              <div className="section-header">
                <h2>Data & Privacy</h2>
                <p>Manage your data and account settings</p>
              </div>

              <div className="setting-group">
                <h3>Data Export</h3>
                <div className="action-item">
                  <div className="action-info">
                    <h4>Download your data</h4>
                    <p>Export all your data including profile, exam history, and settings</p>
                  </div>
                  <button className="btn btn-outline" onClick={exportData}>
                    <i className="fas fa-download"></i>
                    Export Data
                  </button>
                </div>
              </div>

              <div className="setting-group">
                <h3>Account Management</h3>
                <div className="action-item">
                  <div className="action-info">
                    <h4>Reset settings</h4>
                    <p>Reset all settings to their default values</p>
                  </div>
                  <button className="btn btn-secondary" onClick={resetSettings}>
                    <i className="fas fa-undo"></i>
                    Reset Settings
                  </button>
                </div>
                
                <div className="action-item danger">
                  <div className="action-info">
                    <h4>Delete account</h4>
                    <p>Permanently delete your account and all associated data</p>
                  </div>
                  <button className="btn btn-danger" onClick={deleteAccount}>
                    <i className="fas fa-trash"></i>
                    Delete Account
                  </button>
                </div>
              </div>
            </div>
          )}

          <div className="settings-footer">
            <button className="btn btn-primary" onClick={saveAllSettings}>
              <i className="fas fa-save"></i>
              Save All Settings
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
