// File: c:\Users\KIIT0001\Desktop\exam_site\front-end\src\pages\Dashboard.js
import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import Loading from '../components/Loading';
import StatsCard from '../components/StatsCard';
import { toast } from 'react-toastify';
import './Dashboard.css';

const Dashboard = () => {
  const { user, loading: authLoading } = useAuth();
  const navigate = useNavigate();
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    if (!authLoading && user) {
      fetchDashboardData();
      fetchNotifications();
    }
  }, [user, authLoading]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      let endpoint;
      
      // Use CORRECT endpoints matching your backend routes
      switch (user.role) {
        case 'admin':
          endpoint = '/dashboard/admin';
          break;
        case 'teacher':
          endpoint = '/dashboard/teacher';
          break;
        case 'student':
          endpoint = '/dashboard/student';
          break;
        case 'parent':
          endpoint = '/dashboard/parent';
          break;
        default:
          endpoint = '/dashboard/student'; // Default fallback
      }

      console.log(`Fetching dashboard data from: ${endpoint}`);
      const response = await api.get(endpoint);
      console.log('Dashboard response:', response);

      if (response.data.success) {
        setDashboardData(response.data.data);
      } else {
        throw new Error(response.data.message || 'Failed to fetch dashboard data');
      }
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      
      // Show specific error message
      if (error.response?.status === 404) {
        toast.error('Dashboard endpoint not found. Please check server configuration.');
      } else if (error.response?.status === 403) {
        toast.error('Access denied. Please check your role permissions.');
      } else {
        toast.error('Failed to load dashboard data');
      }
    } finally {
      setLoading(false);
    }
  };

  const fetchNotifications = async () => {
    try {
      const response = await api.get('/notifications', {
        params: { limit: 5, unread: true }
      });
      if (response.data.success) {
        setNotifications(response.data.data.notifications || []);
      }
    } catch (error) {
      console.error('Error fetching notifications:', error);
      // Don't show error toast for notifications as it's not critical
    }
  };

  const handleRoleBasedRedirect = () => {
    switch (user.role) {
      case 'admin':
        navigate('/admin/dashboard');
        break;
      case 'teacher':
        navigate('/teacher/dashboard');
        break;
      case 'student':
        navigate('/student/dashboard');
        break;
      case 'parent':
        navigate('/parent/dashboard');
        break;
      default:
        navigate('/dashboard');
    }
  };

  if (authLoading || loading) {
    return <Loading />;
  }

  if (!user) {
    navigate('/login');
    return null;
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <div className="welcome-section">
          <h1>Welcome back, {user.firstName || user.name || 'User'}!</h1>
          <div className="user-info">
            <span className="user-role">Role: {user.role}</span>
            <span className="last-login">
              Last login: {user.lastLogin ? new Date(user.lastLogin).toLocaleDateString() : '7/7/2025'}
            </span>
          </div>
        </div>
        <div className="dashboard-actions">
          <button 
            className="btn btn-primary"
            onClick={handleRoleBasedRedirect}
          >
            Go to {user.role} Dashboard
          </button>
        </div>
      </div>

      <div className="dashboard-content">
        {/* Quick Stats */}
        <div className="quick-stats">
          {dashboardData?.stats && (
            <div className="stats-grid">
              <StatsCard
                title="Total Activities"
                value={dashboardData.stats.totalExams || dashboardData.stats.totalUsers || 0}
                icon="fas fa-chart-line"
                trend="up"
              />
              <StatsCard
                title="Completed"
                value={dashboardData.stats.completedExams || dashboardData.stats.activeExams || 0}
                icon="fas fa-check-circle"
                trend="up"
              />
              <StatsCard
                title="Pending"
                value={dashboardData.stats.pendingResults || dashboardData.stats.pendingGrading || 0}
                icon="fas fa-hourglass-half"
                trend="neutral"
              />
              <StatsCard
                title="Average Score"
                value={`${dashboardData.stats.averageScore || 0}%`}
                icon="fas fa-star"
                trend={dashboardData.stats.averageScore > 70 ? 'up' : 'down'}
              />
            </div>
          )}
        </div>

        <div className="dashboard-grid">
          {/* Recent Activity */}
          <div className="recent-activity">
            <div className="section-header">
              <h2>Recent Activity</h2>
              <Link to="/activity" className="view-all-btn">View All</Link>
            </div>
            <div className="activity-list">
              {dashboardData?.upcomingExams?.length > 0 ? (
                dashboardData.upcomingExams.slice(0, 3).map((exam) => (
                  <div key={exam._id} className="activity-item">
                    <div className="activity-icon">
                      <i className="fas fa-clipboard-check"></i>
                    </div>
                    <div className="activity-content">
                      <h3>{exam.title}</h3>
                      <p>Subject: {exam.subject}</p>
                      <p>
                        Scheduled: {new Date(exam.schedule?.startDate).toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                ))
              ) : dashboardData?.recentResults?.length > 0 ? (
                dashboardData.recentResults.slice(0, 3).map((result) => (
                  <div key={result._id} className="activity-item">
                    <div className="activity-icon">
                      <i className="fas fa-chart-bar"></i>
                    </div>
                    <div className="activity-content">
                      <h3>{result.exam.title}</h3>
                      <p>Score: {result.percentage}%</p>
                      <p>
                        Completed: {new Date(result.submittedAt).toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                ))
              ) : (
                <div className="no-activity">
                  <i className="fas fa-inbox"></i>
                  <p>No recent activity</p>
                </div>
              )}
            </div>
          </div>

          {/* Notifications */}
          <div className="notifications-section">
            <div className="section-header">
              <h2>Recent Notifications</h2>
              <Link to="/notifications" className="view-all-btn">View All</Link>
            </div>
            <div className="notifications-list">
              {notifications.length > 0 ? (
                notifications.map((notification) => (
                  <div key={notification._id} className="notification-item">
                    <div className="notification-icon">
                      <i className="fas fa-bell"></i>
                    </div>
                    <div className="notification-content">
                      <h4>{notification.title}</h4>
                      <p>{notification.message}</p>
                      <span className="notification-time">
                        {new Date(notification.createdAt).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                ))
              ) : (
                <div className="no-notifications">
                  <i className="fas fa-bell-slash"></i>
                  <p>No new notifications</p>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* System Information */}
        <div className="system-info">
          <h2>System Information</h2>
          <div className="info-grid">
            <div className="info-item">
              <span>Server Status:</span>
              <span className="status online">Online</span>
            </div>
            <div className="info-item">
              <span>Last Backup:</span>
              <span>7/7/2025</span>
            </div>
            <div className="info-item">
              <span>Version:</span>
              <span>v2.1.0</span>
            </div>
            <div className="info-item">
              <span>User Role:</span>
              <span>{user.role}</span>
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="quick-actions">
          <h2>Quick Actions</h2>
          <div className="actions-grid">
            {user.role === 'student' && (
              <>
                <Link to="/exams" className="action-btn">
                  <i className="fas fa-clipboard-list"></i>
                  <span>Available Exams</span>
                </Link>
                <Link to="/results" className="action-btn">
                  <i className="fas fa-chart-bar"></i>
                  <span>My Results</span>
                </Link>
              </>
            )}
            {user.role === 'teacher' && (
              <>
                <Link to="/create-exam" className="action-btn">
                  <i className="fas fa-plus"></i>
                  <span>Create Exam</span>
                </Link>
                <Link to="/manage-exams" className="action-btn">
                  <i className="fas fa-cog"></i>
                  <span>Manage Exams</span>
                </Link>
              </>
            )}
            {user.role === 'admin' && (
              <>
                <Link to="/users" className="action-btn">
                  <i className="fas fa-users"></i>
                  <span>Manage Users</span>
                </Link>
                <Link to="/system-settings" className="action-btn">
                  <i className="fas fa-cog"></i>
                  <span>System Settings</span>
                </Link>
              </>
            )}
            <Link to="/profile" className="action-btn">
              <i className="fas fa-user"></i>
              <span>Profile</span>
            </Link>
            <Link to="/help" className="action-btn">
              <i className="fas fa-question-circle"></i>
              <span>Help</span>
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
