import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import Loading from '../components/Loading';
import './CreateExam.css';

const CreateExam = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);
  const [examData, setExamData] = useState({
    title: '',
    subject: '',
    description: '',
    grade: '',
    duration: 60,
    passingMarks: 60,
    examType: 'practice',
    difficulty: 'medium',
    schedule: {
      startDate: '',
      endDate: '',
      timezone: 'UTC'
    },
    settings: {
      randomizeQuestions: false,
      randomizeOptions: false,
      allowReview: true,
      showResultsImmediately: true,
      showCorrectAnswers: false,
      allowMultipleAttempts: false,
      maxAttempts: 1,
      proctoring: {
        enabled: false,
        preventTabSwitching: true,
        requireFullscreen: true,
        disableRightClick: true,
        preventCopyPaste: true,
        enableCamera: false,
        enableMicrophone: false
      }
    },
    instructions: '',
    eligibleStudents: [],
    tags: []
  });

  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState({
    type: 'multiple-choice',
    question: '',
    options: ['', '', '', ''],
    correctAnswer: '',
    points: 1,
    explanation: '',
    image: null
  });

  const handleExamDataChange = (field, value) => {
    if (field.includes('.')) {
      const [parent, child] = field.split('.');
      setExamData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setExamData(prev => ({
        ...prev,
        [field]: value
      }));
    }
  };

  const handleSettingChange = (field, value) => {
    if (field.includes('.')) {
      const [parent, child] = field.split('.');
      setExamData(prev => ({
        ...prev,
        settings: {
          ...prev.settings,
          [parent]: {
            ...prev.settings[parent],
            [child]: value
          }
        }
      }));
    } else {
      setExamData(prev => ({
        ...prev,
        settings: {
          ...prev.settings,
          [field]: value
        }
      }));
    }
  };

  const handleQuestionChange = (field, value) => {
    setCurrentQuestion(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleOptionChange = (index, value) => {
    const newOptions = [...currentQuestion.options];
    newOptions[index] = value;
    setCurrentQuestion(prev => ({
      ...prev,
      options: newOptions
    }));
  };

  const addOption = () => {
    setCurrentQuestion(prev => ({
      ...prev,
      options: [...prev.options, '']
    }));
  };

  const removeOption = (index) => {
    if (currentQuestion.options.length > 2) {
      const newOptions = currentQuestion.options.filter((_, i) => i !== index);
      setCurrentQuestion(prev => ({
        ...prev,
        options: newOptions
      }));
    }
  };

  const validateQuestion = () => {
    if (!currentQuestion.question.trim()) {
      alert('Question text is required');
      return false;
    }

    if (currentQuestion.type === 'multiple-choice' || currentQuestion.type === 'true-false') {
      const validOptions = currentQuestion.options.filter(opt => opt.trim() !== '');
      if (validOptions.length < 2) {
        alert('At least 2 options are required for multiple choice questions');
        return false;
      }
      
      if (!currentQuestion.correctAnswer || !validOptions.includes(currentQuestion.correctAnswer)) {
        alert('Please select a valid correct answer');
        return false;
      }
    }

    if (currentQuestion.points <= 0) {
      alert('Points must be greater than 0');
      return false;
    }

    return true;
  };

  const addQuestion = () => {
    if (!validateQuestion()) return;

    const questionToAdd = {
      ...currentQuestion,
      id: Date.now(),
      options: currentQuestion.options.filter(opt => opt.trim() !== '')
    };

    setQuestions(prev => [...prev, questionToAdd]);
    
    setCurrentQuestion({
      type: 'multiple-choice',
      question: '',
      options: ['', '', '', ''],
      correctAnswer: '',
      points: 1,
      explanation: '',
      image: null
    });

    alert('Question added successfully');
  };

  const removeQuestion = (id) => {
    setQuestions(prev => prev.filter(q => q.id !== id));
    alert('Question removed');
  };

  const validateExamData = () => {
    if (!examData.title.trim()) {
      alert('Exam title is required');
      return false;
    }

    if (examData.title.length < 3) {
      alert('Exam title must be at least 3 characters');
      return false;
    }

    if (!examData.description.trim()) {
      alert('Exam description is required');
      return false;
    }

    if (examData.description.length < 10) {
      alert('Exam description must be at least 10 characters');
      return false;
    }

    if (!examData.subject) {
      alert('Subject is required');
      return false;
    }

    if (!examData.grade) {
      alert('Grade is required');
      return false;
    }

    if (!examData.duration || examData.duration < 1) {
      alert('Duration must be at least 1 minute');
      return false;
    }

    if (!examData.passingMarks || examData.passingMarks < 0) {
      alert('Passing marks must be 0 or greater');
      return false;
    }

    if (!examData.schedule.startDate) {
      alert('Start date is required');
      return false;
    }

    if (!examData.schedule.endDate) {
      alert('End date is required');
      return false;
    }

    const startDate = new Date(examData.schedule.startDate);
    const endDate = new Date(examData.schedule.endDate);

    if (startDate >= endDate) {
      alert('End date must be after start date');
      return false;
    }

    if (startDate < new Date()) {
      alert('Start date cannot be in the past');
      return false;
    }

    return true;
  };

  const handleSubmit = async () => {
    if (!validateExamData()) return;
    
    if (questions.length === 0) {
      alert('Please add at least one question');
      return;
    }

    setLoading(true);
    
    try {
      const examPayload = {
        title: examData.title.trim(),
        description: examData.description.trim(),
        subject: examData.subject,
        grade: examData.grade,
        duration: parseInt(examData.duration),
        passingMarks: parseInt(examData.passingMarks),
        examType: examData.examType,
        difficulty: examData.difficulty,
        schedule: {
          startDate: examData.schedule.startDate,
          endDate: examData.schedule.endDate,
          timezone: examData.schedule.timezone
        },
        settings: examData.settings,
        instructions: examData.instructions.trim(),
        eligibleStudents: examData.eligibleStudents,
        tags: examData.tags
      };

      console.log('Submitting exam payload:', examPayload);

      const response = await api.post('/exams', examPayload);
      
      if (response.data.success) {
        const examId = response.data.data.exam._id;
        
        if (questions.length > 0) {
          const questionPromises = questions.map(async (question) => {
            const questionPayload = {
              type: question.type,
              question: question.question.trim(),
              options: question.options,
              correctAnswer: question.correctAnswer,
              points: parseInt(question.points),
              explanation: question.explanation?.trim() || ''
            };
            
            return api.post(`/exams/${examId}/questions`, questionPayload);
          });
          
          await Promise.all(questionPromises);
        }
        
        alert('Exam created successfully!');
        navigate('/exams');
      }
    } catch (error) {
      console.error('Error creating exam:', error);
      
      if (error.response?.data?.errors) {
        error.response.data.errors.forEach(err => {
          alert(`${err.path || err.param}: ${err.msg}`);
        });
      } else if (error.response?.data?.message) {
        alert(error.response.data.message);
      } else {
        alert('Error creating exam. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const nextStep = () => {
    if (currentStep === 1 && !validateExamData()) return;
    setCurrentStep(prev => prev + 1);
  };

  const prevStep = () => {
    setCurrentStep(prev => prev - 1);
  };

  const getTotalPoints = () => {
    return questions.reduce((total, question) => total + question.points, 0);
  };

  const generateDateTimeLocal = (daysFromNow = 0, hours = 9) => {
    const date = new Date();
    date.setDate(date.getDate() + daysFromNow);
    date.setHours(hours, 0, 0, 0);
    return date.toISOString().slice(0, 16);
  };

  if (loading) return <Loading />;

  return (
    <div className="create-exam-container">
      <div className="create-exam-header">
        <h1>Create New Exam</h1>
        <div className="step-indicator">
          <div className={`step ${currentStep >= 1 ? 'active' : ''}`}>
            <span>1</span>
            <label>Exam Details</label>
          </div>
          <div className={`step ${currentStep >= 2 ? 'active' : ''}`}>
            <span>2</span>
            <label>Questions</label>
          </div>
          <div className={`step ${currentStep >= 3 ? 'active' : ''}`}>
            <span>3</span>
            <label>Review & Submit</label>
          </div>
        </div>
      </div>

      {currentStep === 1 && (
        <div className="step-content">
          <h2>Exam Information</h2>
          
          <div className="form-group">
            <label>Exam Title *</label>
            <input
              type="text"
              value={examData.title}
              onChange={(e) => handleExamDataChange('title', e.target.value)}
              placeholder="Enter exam title"
              className="form-control"
              required
            />
          </div>

          <div className="form-group">
            <label>Description *</label>
            <textarea
              value={examData.description}
              onChange={(e) => handleExamDataChange('description', e.target.value)}
              placeholder="Enter exam description"
              className="form-control"
              rows={4}
              required
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Subject *</label>
              <select
                value={examData.subject}
                onChange={(e) => handleExamDataChange('subject', e.target.value)}
                className="form-control"
                required
              >
                <option value="">Select Subject</option>
                <option value="Mathematics">Mathematics</option>
                <option value="Physics">Physics</option>
                <option value="Chemistry">Chemistry</option>
                <option value="Biology">Biology</option>
                <option value="English">English</option>
                <option value="History">History</option>
                <option value="Geography">Geography</option>
                <option value="Computer Science">Computer Science</option>
                <option value="Economics">Economics</option>
                <option value="Other">Other</option>
              </select>
            </div>

            <div className="form-group">
              <label>Grade *</label>
              <select
                value={examData.grade}
                onChange={(e) => handleExamDataChange('grade', e.target.value)}
                className="form-control"
                required
              >
                <option value="">Select Grade</option>
                <option value="1">Grade 1</option>
                <option value="2">Grade 2</option>
                <option value="3">Grade 3</option>
                <option value="4">Grade 4</option>
                <option value="5">Grade 5</option>
                <option value="6">Grade 6</option>
                <option value="7">Grade 7</option>
                <option value="8">Grade 8</option>
                <option value="9">Grade 9</option>
                <option value="10">Grade 10</option>
                <option value="11">Grade 11</option>
                <option value="12">Grade 12</option>
              </select>
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Duration (minutes) *</label>
              <input
                type="number"
                value={examData.duration}
                onChange={(e) => handleExamDataChange('duration', parseInt(e.target.value) || 0)}
                min="1"
                className="form-control"
                required
              />
            </div>

            <div className="form-group">
              <label>Passing Marks *</label>
              <input
                type="number"
                value={examData.passingMarks}
                onChange={(e) => handleExamDataChange('passingMarks', parseInt(e.target.value) || 0)}
                min="0"
                className="form-control"
                required
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Exam Type</label>
              <select
                value={examData.examType}
                onChange={(e) => handleExamDataChange('examType', e.target.value)}
                className="form-control"
              >
                <option value="practice">Practice</option>
                <option value="mock">Mock</option>
                <option value="final">Final</option>
                <option value="assignment">Assignment</option>
              </select>
            </div>

            <div className="form-group">
              <label>Difficulty</label>
              <select
                value={examData.difficulty}
                onChange={(e) => handleExamDataChange('difficulty', e.target.value)}
                className="form-control"
              >
                <option value="easy">Easy</option>
                <option value="medium">Medium</option>
                <option value="hard">Hard</option>
              </select>
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Start Date & Time *</label>
              <input
                type="datetime-local"
                value={examData.schedule.startDate}
                onChange={(e) => handleExamDataChange('schedule.startDate', e.target.value)}
                min={generateDateTimeLocal()}
                className="form-control"
                required
              />
            </div>

            <div className="form-group">
              <label>End Date & Time *</label>
              <input
                type="datetime-local"
                value={examData.schedule.endDate}
                onChange={(e) => handleExamDataChange('schedule.endDate', e.target.value)}
                min={examData.schedule.startDate || generateDateTimeLocal()}
                className="form-control"
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label>Instructions</label>
            <textarea
              value={examData.instructions}
              onChange={(e) => handleExamDataChange('instructions', e.target.value)}
              placeholder="Enter exam instructions for students"
              className="form-control"
              rows={3}
            />
          </div>

          <div className="settings-section">
            <h3>Exam Settings</h3>
            
            <div className="checkbox-group">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={examData.settings.randomizeQuestions}
                  onChange={(e) => handleSettingChange('randomizeQuestions', e.target.checked)}
                />
                Randomize Questions
              </label>
              
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={examData.settings.randomizeOptions}
                  onChange={(e) => handleSettingChange('randomizeOptions', e.target.checked)}
                />
                Randomize Options
              </label>
              
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={examData.settings.allowReview}
                  onChange={(e) => handleSettingChange('allowReview', e.target.checked)}
                />
                Allow Review
              </label>
              
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={examData.settings.showResultsImmediately}
                  onChange={(e) => handleSettingChange('showResultsImmediately', e.target.checked)}
                />
                Show Results Immediately
              </label>
              
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={examData.settings.showCorrectAnswers}
                  onChange={(e) => handleSettingChange('showCorrectAnswers', e.target.checked)}
                />
                Show Correct Answers
              </label>
              
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={examData.settings.allowMultipleAttempts}
                  onChange={(e) => handleSettingChange('allowMultipleAttempts', e.target.checked)}
                />
                Allow Multiple Attempts
              </label>
            </div>

            {examData.settings.allowMultipleAttempts && (
              <div className="form-group">
                <label>Maximum Attempts</label>
                <input
                  type="number"
                  value={examData.settings.maxAttempts}
                  onChange={(e) => handleSettingChange('maxAttempts', parseInt(e.target.value) || 1)}
                  min="1"
                  max="10"
                  className="form-control"
                />
              </div>
            )}
          </div>

          <div className="step-actions">
            <button className="btn btn-primary" onClick={nextStep}>
              Next: Add Questions
            </button>
          </div>
        </div>
      )}

      {currentStep === 2 && (
        <div className="step-content">
          <h2>Add Questions</h2>
          
          <div className="question-form">
            <div className="form-group">
              <label>Question Type</label>
              <select
                value={currentQuestion.type}
                onChange={(e) => handleQuestionChange('type', e.target.value)}
                className="form-control"
              >
                <option value="multiple-choice">Multiple Choice</option>
                <option value="true-false">True/False</option>
                <option value="short-answer">Short Answer</option>
                <option value="essay">Essay</option>
              </select>
            </div>

            <div className="form-group">
              <label>Question Text *</label>
              <textarea
                value={currentQuestion.question}
                onChange={(e) => handleQuestionChange('question', e.target.value)}
                placeholder="Enter your question"
                className="form-control"
                rows={3}
                required
              />
            </div>

            {(currentQuestion.type === 'multiple-choice' || currentQuestion.type === 'true-false') && (
              <div className="options-section">
                <label>Options</label>
                {currentQuestion.options.map((option, index) => (
                  <div key={index} className="option-input">
                    <input
                      type="text"
                      value={option}
                      onChange={(e) => handleOptionChange(index, e.target.value)}
                      placeholder={`Option ${index + 1}`}
                      className="form-control"
                    />
                    {currentQuestion.options.length > 2 && (
                      <button
                        type="button"
                        onClick={() => removeOption(index)}
                        className="btn btn-danger btn-sm"
                      >
                        <i className="fas fa-trash"></i>
                      </button>
                    )}
                  </div>
                ))}
                
                {currentQuestion.type === 'multiple-choice' && currentQuestion.options.length < 6 && (
                  <button
                    type="button"
                    onClick={addOption}
                    className="btn btn-outline btn-sm"
                  >
                    <i className="fas fa-plus"></i> Add Option
                  </button>
                )}

                <div className="form-group">
                  <label>Correct Answer *</label>
                  <select
                    value={currentQuestion.correctAnswer}
                    onChange={(e) => handleQuestionChange('correctAnswer', e.target.value)}
                    className="form-control"
                    required
                  >
                    <option value="">Select correct answer</option>
                    {currentQuestion.options
                      .filter(opt => opt.trim() !== '')
                      .map((option, index) => (
                        <option key={index} value={option}>
                          {option}
                        </option>
                      ))}
                  </select>
                </div>
              </div>
            )}

            <div className="form-row">
              <div className="form-group">
                <label>Points *</label>
                <input
                  type="number"
                  value={currentQuestion.points}
                  onChange={(e) => handleQuestionChange('points', parseInt(e.target.value) || 1)}
                  min="1"
                  className="form-control"
                  required
                />
              </div>
            </div>

            <div className="form-group">
              <label>Explanation (Optional)</label>
              <textarea
                value={currentQuestion.explanation}
                onChange={(e) => handleQuestionChange('explanation', e.target.value)}
                placeholder="Explain the correct answer"
                className="form-control"
                rows={2}
              />
            </div>

            <div className="question-actions">
              <button
                type="button"
                onClick={addQuestion}
                className="btn btn-success"
              >
                <i className="fas fa-plus"></i> Add Question
              </button>
            </div>
          </div>

          {questions.length > 0 && (
            <div className="questions-list">
              <h3>Added Questions ({questions.length})</h3>
              <div className="questions-summary">
                <p>Total Points: {getTotalPoints()}</p>
              </div>
              
              {questions.map((question, index) => (
                <div key={question.id} className="question-item">
                  <div className="question-header">
                    <span className="question-number">Q{index + 1}</span>
                    <span className="question-type">{question.type}</span>
                    <span className="question-points">{question.points} pts</span>
                    <button
                      onClick={() => removeQuestion(question.id)}
                      className="btn btn-danger btn-sm"
                    >
                      <i className="fas fa-trash"></i>
                    </button>
                  </div>
                  <div className="question-text">
                    {question.question}
                  </div>
                  {question.options && question.options.length > 0 && (
                    <div className="question-options">
                      {question.options.map((option, optIndex) => (
                        <div
                          key={optIndex}
                          className={`option ${option === question.correctAnswer ? 'correct' : ''}`}
                        >
                          {option}
                          {option === question.correctAnswer && (
                            <i className="fas fa-check correct-indicator"></i>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}

          <div className="step-actions">
            <button className="btn btn-outline" onClick={prevStep}>
              Previous
            </button>
            <button
              className="btn btn-primary"
              onClick={nextStep}
              disabled={questions.length === 0}
            >
              Next: Review & Submit
            </button>
          </div>
        </div>
      )}

      {currentStep === 3 && (
        <div className="step-content">
          <h2>Review & Submit</h2>
          
          <div className="exam-review">
            <div className="review-section">
              <h3>Exam Details</h3>
              <div className="review-item">
                <strong>Title:</strong> {examData.title}
              </div>
              <div className="review-item">
                <strong>Subject:</strong> {examData.subject}
              </div>
              <div className="review-item">
                <strong>Grade:</strong> {examData.grade}
              </div>
              <div className="review-item">
                <strong>Duration:</strong> {examData.duration} minutes
              </div>
              <div className="review-item">
                <strong>Passing Marks:</strong> {examData.passingMarks}
              </div>
              <div className="review-item">
                <strong>Start:</strong> {examData.schedule.startDate ? new Date(examData.schedule.startDate).toLocaleString() : 'Not set'}
              </div>
              <div className="review-item">
                <strong>End:</strong> {examData.schedule.endDate ? new Date(examData.schedule.endDate).toLocaleString() : 'Not set'}
              </div>
            </div>

            <div className="review-section">
              <h3>Questions Summary</h3>
              <div className="review-item">
                <strong>Total Questions:</strong> {questions.length}
              </div>
              <div className="review-item">
                <strong>Total Points:</strong> {getTotalPoints()}
              </div>
              <div className="review-item">
                <strong>Question Types:</strong>
                <ul>
                  {Object.entries(
                    questions.reduce((acc, q) => {
                      acc[q.type] = (acc[q.type] || 0) + 1;
                      return acc;
                    }, {})
                  ).map(([type, count]) => (
                    <li key={type}>{type}: {count}</li>
                  ))}
                </ul>
              </div>
            </div>

            <div className="review-section">
              <h3>Settings</h3>
              <div className="settings-review">
                {Object.entries(examData.settings)
                  .filter(([key, value]) => typeof value === 'boolean' && value)
                  .map(([key]) => (
                    <div key={key} className="setting-item">
                      <i className="fas fa-check"></i>
                      {key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}
                    </div>
                  ))}
              </div>
            </div>
          </div>

          <div className="step-actions">
            <button className="btn btn-outline" onClick={prevStep}>
              Previous
            </button>
            <button className="btn btn-success" onClick={handleSubmit}>
              <i className="fas fa-save"></i> Create Exam
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CreateExam;
