import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Breadcrumb from '../components/common/Breadcrumb';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import { formatDate, formatDuration } from '../utils/formatters';
import { useAuth } from '../contexts/AuthContext';
import '../styles/ExamList.css';

const ExamList = () => {
  const { user } = useAuth();
  const [exams, setExams] = useState([]);
  const [filteredExams, setFilteredExams] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    status: 'all',
    subject: 'all',
    search: '',
    sortBy: 'startTime',
    sortOrder: 'asc'
  });

  useEffect(() => {
    fetchExams();
  }, []);

  useEffect(() => {
    if (exams.length) {
      filterAndSortExams();
    }
  }, [exams, filters]);

  const fetchExams = async () => {
    setLoading(true);
    try {
      const response = await axios.get('/api/exams/available');
      setExams(response.data.data);
      setError(null);
    } catch (err) {
      console.error('Error fetching exams:', err);
      setError('Failed to load available exams. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const filterAndSortExams = () => {
    let filtered = [...exams];

    // Extract unique subjects
    const uniqueSubjects = [...new Set(exams.map(exam => exam.subject))];
    setSubjects(uniqueSubjects);

    // Filter by status
    if (filters.status !== 'all') {
      const now = new Date();
      filtered = filtered.filter(exam => {
        // Fix: Handle both date formats consistently
        const startTime = new Date(exam.schedule?.startDate || exam.startTime);
        const endTime = new Date(exam.schedule?.endDate || exam.endTime);
        
        switch (filters.status) {
          case 'upcoming':
            return now < startTime;
          case 'ongoing':
            return now >= startTime && now <= endTime;
          case 'completed':
            return now > endTime;
          default:
            return true;
        }
      });
    }

    // Filter by subject
    if (filters.subject !== 'all') {
      filtered = filtered.filter(exam => exam.subject === filters.subject);
    }

    // Filter by search
    if (filters.search) {
      const searchLower = filters.search.toLowerCase();
      filtered = filtered.filter(exam =>
        exam.title.toLowerCase().includes(searchLower) ||
        exam.subject.toLowerCase().includes(searchLower) ||
        exam.description?.toLowerCase().includes(searchLower)
      );
    }

    // Sort exams
    filtered.sort((a, b) => {
      // Fix: Handle both formats for sorting as well
      let aValue, bValue;
      
      if (filters.sortBy === 'startTime') {
        aValue = new Date(a.schedule?.startDate || a.startTime);
        bValue = new Date(b.schedule?.startDate || b.startTime);
      } else if (filters.sortBy === 'endTime') {
        aValue = new Date(a.schedule?.endDate || a.endTime);
        bValue = new Date(b.schedule?.endDate || b.endTime);
      } else {
        aValue = a[filters.sortBy];
        bValue = b[filters.sortBy];
      }

      if (filters.sortOrder === 'asc') {
        return aValue < bValue ? -1 : aValue > bValue ? 1 : 0;
      } else {
        return aValue > bValue ? -1 : aValue < bValue ? 1 : 0;
      }
    });

    setFilteredExams(filtered);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const handleSearchChange = (e) => {
    setFilters(prev => ({ ...prev, search: e.target.value }));
  };

  const getExamStatus = (exam) => {
    const now = new Date();
    const startTime = new Date(exam.schedule?.startDate || exam.startTime);
    const endTime = new Date(exam.schedule?.endDate || exam.endTime);

    if (now < startTime) {
      return { status: 'upcoming', className: 'status-upcoming' };
    } else if (now >= startTime && now <= endTime) {
      return { status: 'ongoing', className: 'status-ongoing' };
    } else {
      return { status: 'completed', className: 'status-completed' };
    }
  };

  return (
    <div className="exam-list-page">
      <Breadcrumb items={[{ label: 'Home', link: '/' }, { label: 'Available Exams' }]} />
      
      <div className="page-header">
        <h1>Available Exams</h1>
        {user?.role === 'instructor' && (
          <Link to="/exams/create" className="btn btn-primary">
            <i className="fas fa-plus"></i> Create New Exam
          </Link>
        )}
      </div>
      
      {loading ? (
        <Loader />
      ) : error ? (
        <ErrorMessage message={error} />
      ) : (
        <>
          <div className="filters-container">
            <div className="filters-row">
              <div className="filter-group">
                <label>Status</label>
                <select 
                  name="status" 
                  value={filters.status} 
                  onChange={handleFilterChange}
                >
                  <option value="all">All</option>
                  <option value="upcoming">Upcoming</option>
                  <option value="ongoing">Ongoing</option>
                  <option value="completed">Completed</option>
                </select>
              </div>
              
              <div className="filter-group">
                <label>Subject</label>
                <select 
                  name="subject" 
                  value={filters.subject} 
                  onChange={handleFilterChange}
                >
                  <option value="all">All Subjects</option>
                  {subjects.map(subject => (
                    <option key={subject} value={subject}>{subject}</option>
                  ))}
                </select>
              </div>
              
              <div className="filter-group">
                <label>Sort By</label>
                <select 
                  name="sortBy" 
                  value={filters.sortBy} 
                  onChange={handleFilterChange}
                >
                  <option value="startTime">Start Date</option>
                  <option value="endTime">End Date</option>
                  <option value="title">Title</option>
                  <option value="subject">Subject</option>
                </select>
              </div>
              
              <div className="filter-group">
                <label>Order</label>
                <select 
                  name="sortOrder" 
                  value={filters.sortOrder} 
                  onChange={handleFilterChange}
                >
                  <option value="asc">Ascending</option>
                  <option value="desc">Descending</option>
                </select>
              </div>
              
              <div className="search-box">
                <input 
                  type="text" 
                  placeholder="Search exams..." 
                  value={filters.search}
                  onChange={handleSearchChange}
                />
                <i className="fas fa-search"></i>
              </div>
            </div>
          </div>
          
          {filteredExams.length === 0 ? (
            <div className="no-exams-message">
              <i className="fas fa-clipboard-list"></i>
              <p>No exams match your filters</p>
              <button className="btn btn-outline" onClick={() => setFilters({
                status: 'all',
                subject: 'all',
                search: '',
                sortBy: 'startTime',
                sortOrder: 'asc'
              })}>
                Clear Filters
              </button>
            </div>
          ) : (
            <div className="exam-grid">
              {filteredExams.map(exam => {
                const { status, className } = getExamStatus(exam);
                return (
                  <div key={exam._id} className="exam-card">
                    <div className={`exam-status ${className}`}>
                      {status.charAt(0).toUpperCase() + status.slice(1)}
                    </div>
                    
                    <div className="exam-details">
                      <h3>{exam.title}</h3>
                      <div className="exam-subject">{exam.subject}</div>
                      <div className="exam-info-grid">
                        <div className="info-item">
                          <i className="fas fa-calendar"></i>
                          <span>Start: {formatDate(exam.schedule?.startDate || exam.startTime)}</span>
                        </div>
                        <div className="info-item">
                          <i className="fas fa-clock"></i>
                          <span>Duration: {formatDuration(exam.duration)}</span>
                        </div>
                        <div className="info-item">
                          <i className="fas fa-tasks"></i>
                          <span>Questions: {exam.totalQuestions || '-'}</span>
                        </div>
                        <div className="info-item">
                          <i className="fas fa-star"></i>
                          <span>Total Marks: {exam.totalMarks}</span>
                        </div>
                      </div>
                      
                      {exam.description && (
                        <div className="exam-description">
                          <p>{exam.description.length > 120 ? 
                            `${exam.description.substring(0, 120)}...` : 
                            exam.description}
                          </p>
                        </div>
                      )}
                    </div>
                    
                    <div className="exam-actions">
                      <Link to={`/exams/${exam._id}`} className="btn btn-primary">
                        {user?.role === 'student' ? 
                          (status === 'ongoing' ? 'Take Exam' : 'View Details') : 
                          'View Details'
                        }
                      </Link>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default ExamList;
