import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useExam } from '../context/ExamContext';
import api from '../services/api';
import Loading from '../components/Loading';
import ExamCard from '../components/ExamCard';
import { toast } from 'react-toastify';
import './ExamList.css';

const ExamList = () => {
  const { user } = useAuth();
  const [exams, setExams] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredExams, setFilteredExams] = useState([]);
  const [filters, setFilters] = useState({
    subject: 'all',
    status: 'all',
    difficulty: 'all',
    search: ''
  });
  const [submissions, setSubmissions] = useState([]);

  useEffect(() => {
    fetchExams();
    if (user?.role === 'student') {
      fetchMySubmissions();
    }
  }, [user]);

  const fetchExams = async () => {
    try {
      setLoading(true);
      console.log('=== FETCHING EXAMS DEBUG ===');
      console.log('User role:', user?.role);
      
      const response = await api.get('/exams');
      console.log('Exams API response:', response.data);
      
      if (response.data.success) {
        const fetchedExams = response.data.data.exams || [];
        console.log('Fetched exams count:', fetchedExams.length);
        console.log('Fetched exams:', fetchedExams);
        
        setExams(fetchedExams);
      } else {
        throw new Error(response.data.message || 'Failed to fetch exams');
      }
    } catch (error) {
      console.error('Error fetching exams:', error);
      toast.error('Failed to fetch exams');
      setExams([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    filterExams();
  }, [exams, filters]);

  const fetchMySubmissions = async () => {
    try {
      const response = await api.get('/submissions');
      if (response.data.success) {
        setSubmissions(response.data.data.submissions || []);
      }
    } catch (error) {
      console.error('Error fetching submissions:', error);
    }
  };

  const filterExams = () => {
    let filtered = [...exams];
    console.log('=== FILTERING EXAMS DEBUG ===');
    console.log('Total exams before filter:', filtered.length);
    console.log('Current filters:', filters);

    // Apply search filter
    if (filters.search) {
      filtered = filtered.filter(exam =>
        exam.title?.toLowerCase().includes(filters.search.toLowerCase()) ||
        exam.description?.toLowerCase().includes(filters.search.toLowerCase()) ||
        exam.subject?.toLowerCase().includes(filters.search.toLowerCase())
      );
      console.log('After search filter:', filtered.length);
    }

    // Apply subject filter
    if (filters.subject !== 'all') {
      filtered = filtered.filter(exam => exam.subject === filters.subject);
      console.log('After subject filter:', filtered.length);
    }

    // Apply status filter for students
    if (user?.role === 'student' && filters.status !== 'all') {
      const now = new Date();
      console.log('Current time:', now);
      
      filtered = filtered.filter(exam => {
        // Handle both schedule format and direct time format
        const startTime = new Date(exam.schedule?.startDate || exam.startTime);
        const endTime = new Date(exam.schedule?.endDate || exam.endTime);
        const hasSubmitted = submissions.some(sub => sub.exam?._id === exam._id && sub.isSubmitted);

        console.log(`Exam "${exam.title}":`, {
          startTime,
          endTime,
          hasSubmitted,
          isActive: exam.isActive,
          isPublished: exam.isPublished,
          now
        });

        switch (filters.status) {
          case 'upcoming':
            return now < startTime && !hasSubmitted && exam.isActive && exam.isPublished;
          case 'ongoing':
            return now >= startTime && now <= endTime && !hasSubmitted && exam.isActive && exam.isPublished;
          case 'completed':
            return hasSubmitted || now > endTime;
          case 'available':
            return now >= startTime && now <= endTime && !hasSubmitted && exam.isActive && exam.isPublished;
          default:
            return exam.isActive && exam.isPublished;
        }
      });
      console.log('After status filter:', filtered.length);
    }

    console.log('Final filtered exams:', filtered);
    setFilteredExams(filtered);
  };

  const getExamStatus = (exam) => {
    const now = new Date();
    const startTime = new Date(exam.schedule?.startDate || exam.startTime);
    const endTime = new Date(exam.schedule?.endDate || exam.endTime);
    const hasSubmitted = submissions.some(sub => sub.exam._id === exam._id && sub.isSubmitted);

    if (hasSubmitted) return 'completed';
    if (now < startTime) return 'upcoming';
    if (now >= startTime && now <= endTime) return 'ongoing';
    return 'ended';
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
  };

  if (loading) {
    return <Loading />;
  }

  const subjects = [...new Set(exams.map(exam => exam.subject).filter(Boolean))];

  return (
    <div className="exam-list-container">
      <div className="exam-list-header">
        <h1>
          {user?.role === 'student' ? 'Available Exams' : 
           user?.role === 'teacher' ? 'My Exams' : 'All Exams'}
        </h1>
        <p>
          {user?.role === 'student' ? 'Browse and take your exams' :
           user?.role === 'teacher' ? 'Manage your created exams' : 'System exam overview'}
        </p>
        
        {user?.role === 'teacher' && (
          <Link to="/create-exam" className="btn btn-primary">
            <i className="fas fa-plus"></i>
            Create New Exam
          </Link>
        )}
      </div>

      <div className="exam-filters">
        <div className="search-box">
          <i className="fas fa-search"></i>
          <input
            type="text"
            placeholder="Search exams..."
            value={filters.search}
            onChange={(e) => handleFilterChange('search', e.target.value)}
          />
        </div>

        <div className="filter-controls">
          <select
            value={filters.subject}
            onChange={(e) => handleFilterChange('subject', e.target.value)}
          >
            <option value="all">All Subjects</option>
            {subjects.map(subject => (
              <option key={subject} value={subject}>{subject}</option>
            ))}
          </select>

          {user?.role === 'student' && (
            <select
              value={filters.status}
              onChange={(e) => handleFilterChange('status', e.target.value)}
            >
              <option value="all">All Status</option>
              <option value="upcoming">Upcoming</option>
              <option value="ongoing">Ongoing</option>
              <option value="available">Available</option>
              <option value="completed">Completed</option>
            </select>
          )}
        </div>
      </div>

      <div className="exam-stats">
        <div className="stat-item">
          <span className="stat-number">{exams.length}</span>
          <span className="stat-label">Total Exams</span>
        </div>
        <div className="stat-item">
          <span className="stat-number">{filteredExams.length}</span>
          <span className="stat-label">Filtered Results</span>
        </div>
        {user?.role === 'student' && (
          <>
            <div className="stat-item">
              <span className="stat-number">
                {filteredExams.filter(exam => getExamStatus(exam) === 'ongoing').length}
              </span>
              <span className="stat-label">Available Now</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{submissions.length}</span>
              <span className="stat-label">Completed</span>
            </div>
          </>
        )}
      </div>

      <div className="exams-grid">
        {filteredExams.length === 0 ? (
          <div className="no-exams">
            <i className="fas fa-clipboard-list"></i>
            <h3>No exams found</h3>
            <p>
              {filters.search || filters.subject !== 'all' || filters.status !== 'all'
                ? 'Try adjusting your search filters'
                : user?.role === 'teacher' 
                  ? 'Create your first exam to get started'
                  : 'No exams are currently available'
              }
            </p>
            {user?.role === 'teacher' && (
              <Link to="/create-exam" className="btn btn-primary">
                Create Your First Exam
              </Link>
            )}
          </div>
        ) : (
          filteredExams.map(exam => (
            <div key={exam._id} className="exam-wrapper">
              <ExamCard exam={exam} />
              {user?.role === 'student' && (
                <div className="exam-student-actions">
                  {getExamStatus(exam) === 'ongoing' && (
                    <Link to={`/exam/take/${exam._id}`} className="btn btn-primary btn-block">
                      <i className="fas fa-play"></i>
                      Take Exam
                    </Link>
                  )}
                  {getExamStatus(exam) === 'completed' && (
                    <Link to={`/exam/result/${exam._id}`} className="btn btn-success btn-block">
                      <i className="fas fa-chart-bar"></i>
                      View Result
                    </Link>
                  )}
                  {getExamStatus(exam) === 'upcoming' && (
                    <button className="btn btn-outline btn-block" disabled>
                      <i className="fas fa-clock"></i>
                      Starts {new Date(exam.schedule?.startDate || exam.startTime).toLocaleDateString()}
                    </button>
                  )}
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ExamList;
