import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import Loading from '../components/Loading';
import { toast } from 'react-toastify';
import './Students.css';

const Students = () => {
  const { user } = useAuth();
  const [students, setStudents] = useState([]);
  const [filteredStudents, setFilteredStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [filters, setFilters] = useState({
    grade: 'all',
    status: 'all',
    sortBy: 'name',
    sortOrder: 'asc'
  });

  useEffect(() => {
    fetchStudents();
  }, []);

  useEffect(() => {
    filterAndSortStudents();
  }, [students, searchTerm, filters]);

  const fetchStudents = async () => {
    try {
      setLoading(true);
      const response = await api.get('/users/students/list');
      console.log('Students data:', response.data); // Debug log
      setStudents(response.data.data?.students || []);
    } catch (error) {
      console.error('Error fetching students:', error);
      toast.error('Failed to fetch students');
      setStudents([]);
    } finally {
      setLoading(false);
    }
  };

  const filterAndSortStudents = () => {
    let filtered = [...students];

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(student => 
        `${student.firstName || ''} ${student.lastName || ''}`.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (student.email || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
        (student.studentId || '').toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Grade filter
    if (filters.grade !== 'all') {
      filtered = filtered.filter(student => student.grade === filters.grade);
    }

    // Status filter
    if (filters.status !== 'all') {
      filtered = filtered.filter(student => student.isActive === (filters.status === 'active'));
    }

    // Sort
    filtered.sort((a, b) => {
      let aValue, bValue;
      
      switch (filters.sortBy) {
        case 'name':
          aValue = `${a.firstName || ''} ${a.lastName || ''}`.toLowerCase();
          bValue = `${b.firstName || ''} ${b.lastName || ''}`.toLowerCase();
          break;
        case 'email':
          aValue = (a.email || '').toLowerCase();
          bValue = (b.email || '').toLowerCase();
          break;
        case 'grade':
          aValue = a.grade || '';
          bValue = b.grade || '';
          break;
        case 'joinDate':
          aValue = new Date(a.createdAt || 0);
          bValue = new Date(b.createdAt || 0);
          break;
        default:
          aValue = a[filters.sortBy] || '';
          bValue = b[filters.sortBy] || '';
      }

      if (filters.sortOrder === 'asc') {
        return aValue > bValue ? 1 : -1;
      } else {
        return aValue < bValue ? 1 : -1;
      }
    });

    setFilteredStudents(filtered);
  };

  const handleViewStudent = (student) => {
    setSelectedStudent(student);
    setShowModal(true);
  };

  const updateStudentStatus = async (studentId, isActive) => {
    try {
      await api.put(`/users/${studentId}`, { isActive });
      toast.success(`Student ${isActive ? 'activated' : 'deactivated'} successfully`);
      fetchStudents();
    } catch (error) {
      console.error('Error updating student status:', error);
      toast.error('Failed to update student status');
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const getUniqueGrades = () => {
    const grades = [...new Set(students.map(s => s.grade).filter(Boolean))];
    return grades.sort();
  };

  if (loading) return <Loading />;

  return (
    <div className="students-container">
      <div className="students-header">
        <div className="header-content">
          <h1>Students Management</h1>
          <p>Manage and view student information ({students.length} total students)</p>
        </div>
        
        <div className="header-actions">
          {user.role === 'admin' && (
            <Link to="/admin/add-student" className="btn btn-primary">
              <i className="fas fa-plus"></i>
              Add Student
            </Link>
          )}
        </div>
      </div>

      <div className="students-filters">
        <div className="filters-row">
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Search students by name, email, or ID..."
            className="search-input"
          />
          
          <select
            value={filters.grade}
            onChange={(e) => setFilters({...filters, grade: e.target.value})}
            className="filter-select"
          >
            <option value="all">All Grades</option>
            {getUniqueGrades().map(grade => (
              <option key={grade} value={grade}>Grade {grade}</option>
            ))}
          </select>
          
          <select
            value={filters.status}
            onChange={(e) => setFilters({...filters, status: e.target.value})}
            className="filter-select"
          >
            <option value="all">All Status</option>
            <option value="active">Active</option>
            <option value="inactive">Inactive</option>
          </select>
          
          <select
            value={filters.sortBy}
            onChange={(e) => setFilters({...filters, sortBy: e.target.value})}
            className="filter-select"
          >
            <option value="name">Sort by Name</option>
            <option value="email">Sort by Email</option>
            <option value="grade">Sort by Grade</option>
            <option value="joinDate">Sort by Join Date</option>
          </select>
          
          <button
            className="btn btn-outline"
            onClick={() => setFilters({...filters, sortOrder: filters.sortOrder === 'asc' ? 'desc' : 'asc'})}
            title={`Currently sorting ${filters.sortOrder === 'asc' ? 'ascending' : 'descending'}`}
          >
            <i className={`fas fa-sort-${filters.sortOrder === 'asc' ? 'up' : 'down'}`}></i>
            {filters.sortOrder === 'asc' ? 'A-Z' : 'Z-A'}
          </button>
        </div>
      </div>

      <div className="students-stats">
        <div className="stat-card">
          <div className="stat-value">{students.length}</div>
          <div className="stat-label">Total Students</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{students.filter(s => s.isActive !== false).length}</div>
          <div className="stat-label">Active Students</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{getUniqueGrades().length}</div>
          <div className="stat-label">Different Grades</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{filteredStudents.length}</div>
          <div className="stat-label">Filtered Results</div>
        </div>
      </div>

      {filteredStudents.length === 0 ? (
        <div className="no-students">
          <i className="fas fa-users"></i>
          <h3>No Students Found</h3>
          <p>
            {students.length === 0 
              ? "No students are registered in the system yet."
              : "No students match your current search criteria."
            }
          </p>
          {searchTerm && (
            <button 
              className="btn btn-outline"
              onClick={() => setSearchTerm('')}
            >
              Clear Search
            </button>
          )}
        </div>
      ) : (
        <div className="students-table">
          <div className="table-container">
            <table className="table">
              <thead>
                <tr>
                  <th>Student</th>
                  <th>Email</th>
                  <th>Grade</th>
                  <th>Join Date</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredStudents.map((student) => (
                  <tr key={student._id}>
                    <td>
                      <div className="student-info">
                        <div className="student-avatar">
                          {student.avatar || student.profilePicture ? (
                            <img src={student.avatar || student.profilePicture} alt={`${student.firstName} ${student.lastName}`} />
                          ) : (
                            <div className="avatar-placeholder">
                              {(student.firstName || 'U')[0].toUpperCase()}
                              {(student.lastName || 'N')[0].toUpperCase()}
                            </div>
                          )}
                        </div>
                        <div className="student-details">
                          <div className="student-name">
                            {student.firstName || 'Unknown'} {student.lastName || 'User'}
                          </div>
                          <div className="student-id">
                            ID: {student.studentId || student._id?.slice(-8) || 'N/A'}
                          </div>
                        </div>
                      </div>
                    </td>
                    <td>
                      <div className="email-cell">
                        {student.email || 'No email provided'}
                      </div>
                    </td>
                    <td>
                      <div className="grade-cell">
                        {student.grade ? (
                          <span className="grade-badge">Grade {student.grade}</span>
                        ) : (
                          <span className="no-grade">Not assigned</span>
                        )}
                      </div>
                    </td>
                    <td>
                      <div className="date-cell">
                        <div className="join-date">
                          {formatDate(student.createdAt)}
                        </div>
                        {student.createdAt && (
                          <div className="days-ago">
                            {Math.floor((new Date() - new Date(student.createdAt)) / (1000 * 60 * 60 * 24))} days ago
                          </div>
                        )}
                      </div>
                    </td>
                    <td>
                      <span className={`status-badge ${student.isActive !== false ? 'active' : 'inactive'}`}>
                        {student.isActive !== false ? 'Active' : 'Inactive'}
                      </span>
                    </td>
                    <td>
                      <div className="action-buttons">
                        <button
                          className="btn btn-sm btn-outline"
                          onClick={() => handleViewStudent(student)}
                          title="View Details"
                        >
                          <i className="fas fa-eye"></i>
                        </button>
                        {user.role === 'admin' && (
                          <button
                            className={`btn btn-sm ${student.isActive !== false ? 'btn-warning' : 'btn-success'}`}
                            onClick={() => updateStudentStatus(student._id, student.isActive === false)}
                            title={student.isActive !== false ? 'Deactivate' : 'Activate'}
                          >
                            <i className={`fas ${student.isActive !== false ? 'fa-pause' : 'fa-play'}`}></i>
                          </button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* Student Details Modal */}
      {showModal && selectedStudent && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>Student Details</h2>
              <button className="modal-close" onClick={() => setShowModal(false)}>
                <i className="fas fa-times"></i>
              </button>
            </div>
            
            <div className="modal-body">
              <div className="student-profile">
                <div className="profile-picture">
                  {selectedStudent.avatar || selectedStudent.profilePicture ? (
                    <img src={selectedStudent.avatar || selectedStudent.profilePicture} alt={`${selectedStudent.firstName} ${selectedStudent.lastName}`} />
                  ) : (
                    <div className="avatar-placeholder large">
                      {(selectedStudent.firstName || 'U')[0].toUpperCase()}
                      {(selectedStudent.lastName || 'N')[0].toUpperCase()}
                    </div>
                  )}
                </div>
                <div className="profile-info">
                  <h3>{selectedStudent.firstName || 'Unknown'} {selectedStudent.lastName || 'User'}</h3>
                  <p className="email">{selectedStudent.email || 'No email provided'}</p>
                  <span className={`status-badge ${selectedStudent.isActive !== false ? 'active' : 'inactive'}`}>
                    {selectedStudent.isActive !== false ? 'Active' : 'Inactive'}
                  </span>
                </div>
              </div>
              
              <div className="student-details-grid">
                <div className="detail-item">
                  <label>Student ID:</label>
                  <span>{selectedStudent.studentId || selectedStudent._id?.slice(-8) || 'N/A'}</span>
                </div>
                <div className="detail-item">
                  <label>Grade:</label>
                  <span>{selectedStudent.grade ? `Grade ${selectedStudent.grade}` : 'Not assigned'}</span>
                </div>
                <div className="detail-item">
                  <label>Section:</label>
                  <span>{selectedStudent.section || 'Not assigned'}</span>
                </div>
                <div className="detail-item">
                  <label>Phone:</label>
                  <span>{selectedStudent.phone || 'Not provided'}</span>
                </div>
                <div className="detail-item">
                  <label>Join Date:</label>
                  <span>{formatDate(selectedStudent.createdAt)}</span>
                </div>
                <div className="detail-item">
                  <label>Last Login:</label>
                  <span>{selectedStudent.lastLogin ? formatDate(selectedStudent.lastLogin) : 'Never logged in'}</span>
                </div>
                <div className="detail-item">
                  <label>Date of Birth:</label>
                  <span>{selectedStudent.dateOfBirth ? formatDate(selectedStudent.dateOfBirth) : 'Not provided'}</span>
                </div>
                <div className="detail-item">
                  <label>Parent:</label>
                  <span>
                    {selectedStudent.parentId ? 
                      `${selectedStudent.parentId.firstName || ''} ${selectedStudent.parentId.lastName || ''}`.trim() || 'Parent info incomplete' : 
                      'No parent linked'
                    }
                  </span>
                </div>
              </div>

              {selectedStudent.address && (
                <div className="address-section">
                  <h4>Address</h4>
                  <p>
                    {[
                      selectedStudent.address.street,
                      selectedStudent.address.city,
                      selectedStudent.address.state,
                      selectedStudent.address.zipCode,
                      selectedStudent.address.country
                    ].filter(Boolean).join(', ') || 'No address provided'}
                  </p>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Students;
