// frontend/src/pages/AdminDashboard.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import StatsCard from './StatsCard';
import SearchBar from './SearchBar';
import Pagination from './Pagination';
import Modal from './Modal';
import ConfirmDialog from './ConfirmDialog';
import { toast } from 'react-toastify';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState({});
  const [users, setUsers] = useState([]);
  const [exams, setExams] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedUser, setSelectedUser] = useState(null);
  const [showUserModal, setShowUserModal] = useState(false);
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);
  const [actionType, setActionType] = useState('');
  const [targetId, setTargetId] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  useEffect(() => {
    if (activeTab === 'users') {
      fetchUsers();
    } else if (activeTab === 'exams') {
      fetchExams();
    }
  }, [activeTab, currentPage, searchTerm]);

  const fetchDashboardData = async () => {
    try {
      const response = await api.get('/admin/dashboard');
      setStats(response.data);
    } catch (error) {
      toast.error('Failed to fetch dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await api.get('/admin/users', {
        params: { page: currentPage, search: searchTerm }
      });
      setUsers(response.data.users);
      setTotalPages(response.data.totalPages);
    } catch (error) {
      toast.error('Failed to fetch users');
    }
  };

  const fetchExams = async () => {
    try {
      const response = await api.get('/admin/exams', {
        params: { page: currentPage, search: searchTerm }
      });
      setExams(response.data.exams);
      setTotalPages(response.data.totalPages);
    } catch (error) {
      toast.error('Failed to fetch exams');
    }
  };

  const handleUserAction = async (action, userId) => {
    setActionType(action);
    setTargetId(userId);
    setShowConfirmDialog(true);
  };

  const confirmAction = async () => {
    try {
      if (actionType === 'delete') {
        await api.delete(`/admin/users/${targetId}`);
        toast.success('User deleted successfully');
      } else if (actionType === 'suspend') {
        await api.patch(`/admin/users/${targetId}/suspend`);
        toast.success('User suspended successfully');
      } else if (actionType === 'activate') {
        await api.patch(`/admin/users/${targetId}/activate`);
        toast.success('User activated successfully');
      }
      fetchUsers();
    } catch (error) {
      toast.error('Action failed');
    }
  };

  const handleSearch = (term) => {
    setSearchTerm(term);
    setCurrentPage(1);
  };

  if (loading) {
    return <div className="loading">Loading admin dashboard...</div>;
  }

  return (
    <div className="admin-dashboard">
      <div className="dashboard-header">
        <h1>Admin Dashboard</h1>
        <p>Welcome back, {user?.name}</p>
      </div>

      <div className="dashboard-tabs">
        <button 
          className={activeTab === 'overview' ? 'active' : ''}
          onClick={() => setActiveTab('overview')}
        >
          Overview
        </button>
        <button 
          className={activeTab === 'users' ? 'active' : ''}
          onClick={() => setActiveTab('users')}
        >
          Users
        </button>
        <button 
          className={activeTab === 'exams' ? 'active' : ''}
          onClick={() => setActiveTab('exams')}
        >
          Exams
        </button>
        <button 
          className={activeTab === 'reports' ? 'active' : ''}
          onClick={() => setActiveTab('reports')}
        >
          Reports
        </button>
      </div>

      {activeTab === 'overview' && (
        <div className="overview-tab">
          <div className="stats-grid">
            <StatsCard
              title="Total Users"
              value={stats.totalUsers || 0}
              icon="fas fa-users"
              trend={stats.usersTrend}
            />
            <StatsCard
              title="Total Exams"
              value={stats.totalExams || 0}
              icon="fas fa-clipboard-list"
              trend={stats.examsTrend}
            />
            <StatsCard
              title="Active Sessions"
              value={stats.activeSessions || 0}
              icon="fas fa-user-clock"
              trend={stats.sessionsTrend}
            />
            <StatsCard
              title="Revenue"
              value={`$${stats.totalRevenue || 0}`}
              icon="fas fa-dollar-sign"
              trend={stats.revenueTrend}
            />
          </div>

          <div className="recent-activity">
            <h3>Recent Activity</h3>
            <div className="activity-list">
              {stats.recentActivity?.map((activity, index) => (
                <div key={index} className="activity-item">
                  <div className="activity-icon">
                    <i className={activity.icon}></i>
                  </div>
                  <div className="activity-content">
                    <p>{activity.message}</p>
                    <span className="activity-time">{activity.time}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {activeTab === 'users' && (
        <div className="users-tab">
          <SearchBar
            onSearch={handleSearch}
            placeholder="Search users..."
          />
          
          <div className="users-table">
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Role</th>
                  <th>Status</th>
                  <th>Joined</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map(user => (
                  <tr key={user._id}>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                    <td>
                      <span className={`role-badge ${user.role}`}>
                        {user.role}
                      </span>
                    </td>
                    <td>
                      <span className={`status-badge ${user.status}`}>
                        {user.status}
                      </span>
                    </td>
                    <td>{new Date(user.createdAt).toLocaleDateString()}</td>
                    <td>
                      <div className="action-buttons">
                        <button
                          className="btn-view"
                          onClick={() => {
                            setSelectedUser(user);
                            setShowUserModal(true);
                          }}
                        >
                          View
                        </button>
                        <button
                          className="btn-suspend"
                          onClick={() => handleUserAction('suspend', user._id)}
                        >
                          Suspend
                        </button>
                        <button
                          className="btn-delete"
                          onClick={() => handleUserAction('delete', user._id)}
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <Pagination
            currentPage={currentPage}
            totalPages={totalPages}
            onPageChange={setCurrentPage}
          />
        </div>
      )}

      {/* User Details Modal */}
      <Modal
        isOpen={showUserModal}
        onClose={() => setShowUserModal(false)}
        title="User Details"
      >
        {selectedUser && (
          <div className="user-details">
            <div className="user-info">
              <h3>{selectedUser.name}</h3>
              <p>{selectedUser.email}</p>
              <p>Role: {selectedUser.role}</p>
              <p>Status: {selectedUser.status}</p>
              <p>Joined: {new Date(selectedUser.createdAt).toLocaleDateString()}</p>
            </div>
            {/* Add more user details as needed */}
          </div>
        )}
      </Modal>

      {/* Confirm Dialog */}
      <ConfirmDialog
        isOpen={showConfirmDialog}
        onClose={() => setShowConfirmDialog(false)}
        onConfirm={confirmAction}
        title="Confirm Action"
        message={`Are you sure you want to ${actionType} this user?`}
        type="danger"
      />
    </div>
  );
};

export default AdminDashboard;
