import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import Loading from '../components/Loading';
import { toast } from 'react-toastify';
import './Students.css';

const Students = () => {
  const { user } = useAuth();
  const [students, setStudents] = useState([]);
  const [filteredStudents, setFilteredStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [filters, setFilters] = useState({
    grade: 'all',
    status: 'all',
    sortBy: 'name',
    sortOrder: 'asc'
  });

  useEffect(() => {
    fetchStudents();
  }, []);

  useEffect(() => {
    filterAndSortStudents();
  }, [students, searchTerm, filters]);

  const fetchStudents = async () => {
    try {
      setLoading(true);
      const response = await api.get('/users/students/list');
      setStudents(response.data.data?.students || []);
    } catch (error) {
      console.error('Error fetching students:', error);
      toast.error('Failed to fetch students');
      setStudents([]);
    } finally {
      setLoading(false);
    }
  };

  const filterAndSortStudents = () => {
    let filtered = [...students];

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(student => 
        `${student.firstName} ${student.lastName}`.toLowerCase().includes(searchTerm.toLowerCase()) ||
        student.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (student.studentId && student.studentId.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    // Grade filter
    if (filters.grade !== 'all') {
      filtered = filtered.filter(student => student.grade === filters.grade);
    }

    // Status filter
    if (filters.status !== 'all') {
      filtered = filtered.filter(student => student.isActive === (filters.status === 'active'));
    }

    // Sort
    filtered.sort((a, b) => {
      let aValue, bValue;
      
      switch (filters.sortBy) {
        case 'name':
          aValue = `${a.firstName} ${a.lastName}`.toLowerCase();
          bValue = `${b.firstName} ${b.lastName}`.toLowerCase();
          break;
        case 'email':
          aValue = a.email.toLowerCase();
          bValue = b.email.toLowerCase();
          break;
        case 'grade':
          aValue = a.grade || '';
          bValue = b.grade || '';
          break;
        case 'joinDate':
          aValue = new Date(a.createdAt);
          bValue = new Date(b.createdAt);
          break;
        default:
          aValue = a[filters.sortBy] || '';
          bValue = b[filters.sortBy] || '';
      }

      if (filters.sortOrder === 'asc') {
        return aValue > bValue ? 1 : -1;
      } else {
        return aValue < bValue ? 1 : -1;
      }
    });

    setFilteredStudents(filtered);
  };

  const handleViewStudent = (student) => {
    setSelectedStudent(student);
    setShowModal(true);
  };

  const updateStudentStatus = async (studentId, isActive) => {
    try {
      await api.put(`/users/${studentId}`, { isActive });
      toast.success(`Student ${isActive ? 'activated' : 'deactivated'} successfully`);
      fetchStudents();
    } catch (error) {
      console.error('Error updating student status:', error);
      toast.error('Failed to update student status');
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const getUniqueGrades = () => {
    const grades = [...new Set(students.map(s => s.grade).filter(Boolean))];
    return grades.sort();
  };

  if (loading) return <Loading />;

  return (
    <div className="students-container">
      <div className="students-header">
        <div className="header-content">
          <h1>Students Management</h1>
          <p>Manage and view student information</p>
        </div>
        
        <div className="header-actions">
          {user.role === 'admin' && (
            <Link to="/admin/add-student" className="btn btn-primary">
              <i className="fas fa-plus"></i>
              Add Student
            </Link>
          )}
        </div>
      </div>

      <div className="students-filters">
        <div className="filters-row">
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Search students..."
            className="search-input"
          />
          
          <select
            value={filters.grade}
            onChange={(e) => setFilters({...filters, grade: e.target.value})}
            className="filter-select"
          >
            <option value="all">All Grades</option>
            {getUniqueGrades().map(grade => (
              <option key={grade} value={grade}>{grade}</option>
            ))}
          </select>
          
          <select
            value={filters.status}
            onChange={(e) => setFilters({...filters, status: e.target.value})}
            className="filter-select"
          >
            <option value="all">All Status</option>
            <option value="active">Active</option>
            <option value="inactive">Inactive</option>
          </select>
          
          <select
            value={filters.sortBy}
            onChange={(e) => setFilters({...filters, sortBy: e.target.value})}
            className="filter-select"
          >
            <option value="name">Sort by Name</option>
            <option value="email">Sort by Email</option>
            <option value="grade">Sort by Grade</option>
            <option value="joinDate">Sort by Join Date</option>
          </select>
          
          <button
            className="btn btn-outline"
            onClick={() => setFilters({...filters, sortOrder: filters.sortOrder === 'asc' ? 'desc' : 'asc'})}
          >
            <i className={`fas fa-sort-${filters.sortOrder === 'asc' ? 'up' : 'down'}`}></i>
          </button>
        </div>
      </div>

      <div className="students-stats">
        <div className="stat-card">
          <div className="stat-value">{students.length}</div>
          <div className="stat-label">Total Students</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{students.filter(s => s.isActive).length}</div>
          <div className="stat-label">Active Students</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{getUniqueGrades().length}</div>
          <div className="stat-label">Grades</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{filteredStudents.length}</div>
          <div className="stat-label">Filtered Results</div>
        </div>
      </div>

      {filteredStudents.length === 0 ? (
        <div className="no-students">
          <i className="fas fa-users"></i>
          <h3>No Students Found</h3>
          <p>No students match your current filters.</p>
        </div>
      ) : (
        <div className="students-table">
          <div className="table-container">
            <table className="table">
              <thead>
                <tr>
                  <th>Student</th>
                  <th>Email</th>
                  <th>Grade</th>
                  <th>Join Date</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredStudents.map((student) => (
                  <tr key={student._id}>
                    <td>
                      <div className="student-info">
                        <div className="student-avatar">
                          {student.profilePicture ? (
                            <img src={student.profilePicture} alt={student.firstName} />
                          ) : (
                            <div className="avatar-placeholder">
                              {student.firstName?.[0]}{student.lastName?.[0]}
                            </div>
                          )}
                        </div>
                        <div className="student-details">
                          <div className="student-name">
                            {student.firstName} {student.lastName}
                          </div>
                          <div className="student-id">ID: {student.studentId || 'N/A'}</div>
                        </div>
                      </div>
                    </td>
                    <td>{student.email}</td>
                    <td>{student.grade || 'N/A'}</td>
                    <td>{formatDate(student.createdAt)}</td>
                    <td>
                      <span className={`status-badge ${student.isActive ? 'active' : 'inactive'}`}>
                        {student.isActive ? 'Active' : 'Inactive'}
                      </span>
                    </td>
                    <td>
                      <div className="action-buttons">
                        <button
                          className="btn btn-sm btn-outline"
                          onClick={() => handleViewStudent(student)}
                          title="View Details"
                        >
                          <i className="fas fa-eye"></i>
                        </button>
                        {user.role === 'admin' && (
                          <button
                            className={`btn btn-sm ${student.isActive ? 'btn-warning' : 'btn-success'}`}
                            onClick={() => updateStudentStatus(student._id, !student.isActive)}
                            title={student.isActive ? 'Deactivate' : 'Activate'}
                          >
                            <i className={`fas ${student.isActive ? 'fa-pause' : 'fa-play'}`}></i>
                          </button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* Student Details Modal */}
      {showModal && selectedStudent && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>Student Details</h2>
              <button className="modal-close" onClick={() => setShowModal(false)}>
                <i className="fas fa-times"></i>
              </button>
            </div>
            
            <div className="modal-body">
              <div className="student-profile">
                <div className="profile-picture">
                  {selectedStudent.profilePicture ? (
                    <img src={selectedStudent.profilePicture} alt={selectedStudent.firstName} />
                  ) : (
                    <div className="avatar-placeholder large">
                      {selectedStudent.firstName?.[0]}{selectedStudent.lastName?.[0]}
                    </div>
                  )}
                </div>
                <div className="profile-info">
                  <h3>{selectedStudent.firstName} {selectedStudent.lastName}</h3>
                  <p className="email">{selectedStudent.email}</p>
                  <span className={`status-badge ${selectedStudent.isActive ? 'active' : 'inactive'}`}>
                    {selectedStudent.isActive ? 'Active' : 'Inactive'}
                  </span>
                </div>
              </div>
              
              <div className="student-details-grid">
                <div className="detail-item">
                  <label>Student ID:</label>
                  <span>{selectedStudent.studentId || 'N/A'}</span>
                </div>
                <div className="detail-item">
                  <label>Grade:</label>
                  <span>{selectedStudent.grade || 'N/A'}</span>
                </div>
                <div className="detail-item">
                  <label>Phone:</label>
                  <span>{selectedStudent.phone || 'N/A'}</span>
                </div>
                <div className="detail-item">
                  <label>Join Date:</label>
                  <span>{formatDate(selectedStudent.createdAt)}</span>
                </div>
                <div className="detail-item">
                  <label>Last Login:</label>
                  <span>{selectedStudent.lastLogin ? formatDate(selectedStudent.lastLogin) : 'Never'}</span>
                </div>
                <div className="detail-item">
                  <label>Parent:</label>
                  <span>
                    {selectedStudent.parentId ? 
                      `${selectedStudent.parentId.firstName} ${selectedStudent.parentId.lastName}` : 
                      'No parent linked'
                    }
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Students;
