import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import Loading from '../components/Loading';
import { toast } from 'react-toastify';
import './ResultsList.css';

const ResultsList = () => {
  const { user } = useAuth();
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    subject: 'all',
    status: 'all',
    sortBy: 'date',
    search: ''
  });
  const [filteredResults, setFilteredResults] = useState([]);
  const [stats, setStats] = useState({});

  useEffect(() => {
    fetchResults();
  }, []);

  useEffect(() => {
    filterResults();
    calculateStats();
  }, [results, filters]);

  const fetchResults = async () => {
    try {
      setLoading(true);
      const response = await api.get('/submissions');
      
      if (response.data.success) {
        const submissions = response.data.data.submissions || [];
        
        // Transform submissions to results format
        const transformedResults = submissions
          .filter(sub => sub.isSubmitted)
          .map(sub => ({
            _id: sub._id,
            exam: sub.exam,
            score: sub.totalScore || 0,
            percentage: sub.percentage || Math.round((sub.totalScore || 0) / (sub.exam?.totalMarks || 1) * 100),
            grade: sub.grade || getGradeLetter(sub.percentage || 0),
            isPassed: sub.isPassed || false,
            submittedAt: sub.submittedAt,
            timeTaken: sub.timeTaken || 0,
            isGraded: sub.isGraded || false
          }));

        setResults(transformedResults);
      }
    } catch (error) {
      console.error('Error fetching results:', error);
      toast.error('Failed to fetch results');
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  const filterResults = () => {
    let filtered = [...results];

    // Apply search filter
    if (filters.search) {
      filtered = filtered.filter(result =>
        result.exam?.title?.toLowerCase().includes(filters.search.toLowerCase()) ||
        result.exam?.subject?.toLowerCase().includes(filters.search.toLowerCase())
      );
    }

    // Apply subject filter
    if (filters.subject !== 'all') {
      filtered = filtered.filter(result => result.exam?.subject === filters.subject);
    }

    // Apply status filter
    if (filters.status !== 'all') {
      if (filters.status === 'passed') {
        filtered = filtered.filter(result => result.isPassed);
      } else if (filters.status === 'failed') {
        filtered = filtered.filter(result => !result.isPassed);
      } else if (filters.status === 'graded') {
        filtered = filtered.filter(result => result.isGraded);
      } else if (filters.status === 'pending') {
        filtered = filtered.filter(result => !result.isGraded);
      }
    }

    // Apply sorting
    filtered.sort((a, b) => {
      switch (filters.sortBy) {
        case 'date':
          return new Date(b.submittedAt) - new Date(a.submittedAt);
        case 'score':
          return b.percentage - a.percentage;
        case 'title':
          return (a.exam?.title || '').localeCompare(b.exam?.title || '');
        case 'subject':
          return (a.exam?.subject || '').localeCompare(b.exam?.subject || '');
        default:
          return new Date(b.submittedAt) - new Date(a.submittedAt);
      }
    });

    setFilteredResults(filtered);
  };

  const calculateStats = () => {
    if (results.length === 0) {
      setStats({
        totalExams: 0,
        averageScore: 0,
        passRate: 0,
        bestScore: 0,
        totalTime: 0
      });
      return;
    }

    const totalExams = results.length;
    const passedExams = results.filter(r => r.isPassed).length;
    const scores = results.map(r => r.percentage);
    const times = results.map(r => r.timeTaken || 0);

    setStats({
      totalExams,
      averageScore: Math.round(scores.reduce((sum, score) => sum + score, 0) / scores.length),
      passRate: Math.round((passedExams / totalExams) * 100),
      bestScore: Math.max(...scores),
      totalTime: times.reduce((sum, time) => sum + time, 0)
    });
  };

  const getGradeLetter = (percentage) => {
    if (percentage >= 90) return 'A';
    if (percentage >= 80) return 'B';
    if (percentage >= 70) return 'C';
    if (percentage >= 60) return 'D';
    return 'F';
  };

  const getGradeColor = (percentage) => {
    if (percentage >= 90) return '#28a745';
    if (percentage >= 80) return '#17a2b8';
    if (percentage >= 70) return '#ffc107';
    if (percentage >= 60) return '#fd7e14';
    return '#dc3545';
  };

  const formatTime = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return hours > 0 ? `${hours}h ${mins}m` : `${mins}m`;
  };

  if (loading) {
    return <Loading />;
  }

  const subjects = [...new Set(results.map(r => r.exam?.subject).filter(Boolean))];

  return (
    <div className="results-container">
      <div className="results-header">
        <h1>Exam Results</h1>
        <p>View your exam performance and progress</p>
      </div>

      <div className="results-stats">
        <div className="stat-card">
          <div className="stat-icon">
            <i className="fas fa-clipboard-check"></i>
          </div>
          <div className="stat-content">
            <h3>{stats.totalExams}</h3>
            <p>Total Exams</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">
            <i className="fas fa-chart-line"></i>
          </div>
          <div className="stat-content">
            <h3>{stats.averageScore}%</h3>
            <p>Average Score</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">
            <i className="fas fa-trophy"></i>
          </div>
          <div className="stat-content">
            <h3>{stats.bestScore}%</h3>
            <p>Best Score</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">
            <i className="fas fa-percentage"></i>
          </div>
          <div className="stat-content">
            <h3>{stats.passRate}%</h3>
            <p>Pass Rate</p>
          </div>
        </div>
      </div>

      <div className="results-filters">
        <div className="search-box">
          <i className="fas fa-search"></i>
          <input
            type="text"
            placeholder="Search results..."
            value={filters.search}
            onChange={(e) => setFilters(prev => ({ ...prev, search: e.target.value }))}
          />
        </div>

        <div className="filter-controls">
          <select
            value={filters.subject}
            onChange={(e) => setFilters(prev => ({ ...prev, subject: e.target.value }))}
          >
            <option value="all">All Subjects</option>
            {subjects.map(subject => (
              <option key={subject} value={subject}>{subject}</option>
            ))}
          </select>

          <select
            value={filters.status}
            onChange={(e) => setFilters(prev => ({ ...prev, status: e.target.value }))}
          >
            <option value="all">All Results</option>
            <option value="passed">Passed</option>
            <option value="failed">Failed</option>
            <option value="graded">Graded</option>
            <option value="pending">Pending</option>
          </select>

          <select
            value={filters.sortBy}
            onChange={(e) => setFilters(prev => ({ ...prev, sortBy: e.target.value }))}
          >
            <option value="date">Sort by Date</option>
            <option value="score">Sort by Score</option>
            <option value="title">Sort by Title</option>
            <option value="subject">Sort by Subject</option>
          </select>
        </div>
      </div>

      <div className="results-list">
        {filteredResults.length === 0 ? (
          <div className="no-results">
            <i className="fas fa-chart-bar"></i>
            <h3>No results found</h3>
            <p>
              {filters.search || filters.subject !== 'all' || filters.status !== 'all'
                ? 'Try adjusting your search filters'
                : 'You haven\'t completed any exams yet'
              }
            </p>
            <Link to="/exams" className="btn btn-primary">
              Browse Available Exams
            </Link>
          </div>
        ) : (
          <div className="results-grid">
            {filteredResults.map(result => (
              <div key={result._id} className="result-card">
                <div className="result-header">
                  <h3>{result.exam?.title || 'Unknown Exam'}</h3>
                  <span className="subject-badge">{result.exam?.subject || 'N/A'}</span>
                </div>

                <div className="result-score">
                  <div 
                    className="score-circle"
                    style={{ 
                      borderColor: getGradeColor(result.percentage),
                      color: getGradeColor(result.percentage)
                    }}
                  >
                    <span className="percentage">{result.percentage}%</span>
                    <span className="grade">{result.grade}</span>
                  </div>
                  
                  <div className="score-details">
                    <div className="score-row">
                      <span>Score:</span>
                      <span>{result.score}/{result.exam?.totalMarks || 0}</span>
                    </div>
                    <div className="score-row">
                      <span>Status:</span>
                      <span className={`status ${result.isPassed ? 'passed' : 'failed'}`}>
                        {result.isPassed ? 'Passed' : 'Failed'}
                      </span>
                    </div>
                    <div className="score-row">
                      <span>Time:</span>
                      <span>{formatTime(result.timeTaken)}</span>
                    </div>
                    <div className="score-row">
                      <span>Date:</span>
                      <span>{new Date(result.submittedAt).toLocaleDateString()}</span>
                    </div>
                  </div>
                </div>

                <div className="result-actions">
                  <Link 
                    to={`/exam/result/${result._id}`} 
                    className="btn btn-primary"
                  >
                    <i className="fas fa-eye"></i>
                    View Details
                  </Link>
                  {!result.isGraded && (
                    <span className="grading-status">
                      <i className="fas fa-clock"></i>
                      Grading Pending
                    </span>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ResultsList;
