import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useSettings } from '../context/SettingsContext';
import { useTranslation } from '../hooks/useTranslation';
import Loading from '../components/Loading';
import './Settings.css';

const Settings = () => {
  const { user } = useAuth();
  const { settings, updateSetting, saveAllSettings, resetSettings, loading } = useSettings();
  const { t } = useTranslation();
  const [saving, setSaving] = useState(false);
  const [activeTab, setActiveTab] = useState('general');
  const [message, setMessage] = useState('');

  const showMessage = (text, type = 'info') => {
    setMessage({ text, type });
    setTimeout(() => setMessage(''), 3000);
  };

  const handleSettingChange = async (category, key, value) => {
    const result = await updateSetting(category, key, value);
    if (result.success) {
      showMessage(t('settingsUpdated'), 'success');
    } else {
      showMessage(t('settingsSaveError'), 'error');
    }
  };

  const handleSaveAll = async () => {
    setSaving(true);
    const result = await saveAllSettings();
    if (result.success) {
      showMessage(t('settingsUpdated'), 'success');
    } else {
      showMessage(t('settingsSaveError'), 'error');
    }
    setSaving(false);
  };

  const handleReset = async () => {
    if (window.confirm(t('confirmReset'))) {
      const result = await resetSettings();
      if (result.success) {
        showMessage(t('settingsReset'), 'success');
      } else {
        showMessage(t('settingsSaveError'), 'error');
      }
    }
  };

  if (loading) return <Loading />;

  return (
    <div className="settings-container">
      <div className="settings-header">
        <h1>{t('settingsTitle')}</h1>
        <div className="settings-actions">
          <button 
            className="btn btn-secondary" 
            onClick={handleReset}
            disabled={saving || loading}
          >
            {t('resetToDefault')}
          </button>
          <button 
            className="btn btn-primary" 
            onClick={handleSaveAll}
            disabled={saving || loading}
          >
            {saving ? t('saving') : t('saveAll')}
          </button>
        </div>
      </div>

      {message && (
        <div className={`message ${message.type}`}>
          {message.text}
        </div>
      )}

      <div className="settings-content">
        <div className="settings-tabs">
          <button 
            className={`tab-button ${activeTab === 'general' ? 'active' : ''}`}
            onClick={() => setActiveTab('general')}
          >
            {t('general')}
          </button>
          <button 
            className={`tab-button ${activeTab === 'notifications' ? 'active' : ''}`}
            onClick={() => setActiveTab('notifications')}
          >
            {t('notificationsTab')}
          </button>
          <button 
            className={`tab-button ${activeTab === 'privacy' ? 'active' : ''}`}
            onClick={() => setActiveTab('privacy')}
          >
            {t('privacy')}
          </button>
          <button 
            className={`tab-button ${activeTab === 'exam' ? 'active' : ''}`}
            onClick={() => setActiveTab('exam')}
          >
            {t('examSettings')}
          </button>
        </div>

        <div className="settings-panel">
          {activeTab === 'general' && (
            <div className="settings-section">
              <h3>{t('generalPreferences')}</h3>
              
              <div className="setting-item">
                <label>{t('language')}</label>
                <select 
                  value={settings.preferences.language}
                  onChange={(e) => handleSettingChange('preferences', 'language', e.target.value)}
                >
                  <option value="en">{t('english')}</option>
                  <option value="fr">{t('french')}</option>
                  <option value="es">{t('spanish')}</option>
                  <option value="de">{t('german')}</option>
                </select>
              </div>

              <div className="setting-item">
                <label>{t('timezone')}</label>
                <select 
                  value={settings.preferences.timezone}
                  onChange={(e) => handleSettingChange('preferences', 'timezone', e.target.value)}
                >
                  <option value="UTC">UTC</option>
                  <option value="EST">Eastern Time (EST)</option>
                  <option value="PST">Pacific Time (PST)</option>
                  <option value="GMT">Greenwich Mean Time (GMT)</option>
                  <option value="IST">India Standard Time (IST)</option>
                </select>
              </div>

              <div className="setting-item">
                <label>{t('theme')}</label>
                <select 
                  value={settings.preferences.theme}
                  onChange={(e) => handleSettingChange('preferences', 'theme', e.target.value)}
                >
                  <option value="light">{t('light')}</option>
                  <option value="dark">{t('dark')}</option>
                  <option value="auto">{t('auto')}</option>
                </select>
              </div>

              <div className="setting-item">
                <label>{t('dateFormat')}</label>
                <select 
                  value={settings.preferences.dateFormat}
                  onChange={(e) => handleSettingChange('preferences', 'dateFormat', e.target.value)}
                >
                  <option value="MM/DD/YYYY">MM/DD/YYYY (US)</option>
                  <option value="DD/MM/YYYY">DD/MM/YYYY (EU)</option>
                  <option value="YYYY-MM-DD">YYYY-MM-DD (ISO)</option>
                  <option value="DD-MMM-YYYY">DD-MMM-YYYY</option>
                </select>
              </div>

              <div className="setting-item">
                <label>{t('timeFormat')}</label>
                <select 
                  value={settings.preferences.timeFormat}
                  onChange={(e) => handleSettingChange('preferences', 'timeFormat', e.target.value)}
                >
                  <option value="12h">12 Hour (AM/PM)</option>
                  <option value="24h">24 Hour (Military)</option>
                </select>
              </div>
            </div>
          )}

          {activeTab === 'notifications' && (
            <div className="settings-section">
              <h3>{t('notificationSettings')}</h3>
              
              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.notifications.emailNotifications}
                    onChange={(e) => handleSettingChange('notifications', 'emailNotifications', e.target.checked)}
                  />
                  {t('emailNotifications')}
                </label>
                <p>{t('emailNotificationsDesc')}</p>
              </div>

              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.notifications.pushNotifications}
                    onChange={(e) => handleSettingChange('notifications', 'pushNotifications', e.target.checked)}
                  />
                  {t('pushNotifications')}
                </label>
                <p>{t('pushNotificationsDesc')}</p>
              </div>

              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.notifications.examReminders}
                    onChange={(e) => handleSettingChange('notifications', 'examReminders', e.target.checked)}
                  />
                  {t('examReminders')}
                </label>
                <p>{t('examRemindersDesc')}</p>
              </div>

              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.notifications.resultNotifications}
                    onChange={(e) => handleSettingChange('notifications', 'resultNotifications', e.target.checked)}
                  />
                  {t('resultNotifications')}
                </label>
                <p>{t('resultNotificationsDesc')}</p>
              </div>

              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.notifications.systemUpdates}
                    onChange={(e) => handleSettingChange('notifications', 'systemUpdates', e.target.checked)}
                  />
                  {t('systemUpdates')}
                </label>
                <p>{t('systemUpdatesDesc')}</p>
              </div>
            </div>
          )}

          {activeTab === 'privacy' && (
            <div className="settings-section">
              <h3>{t('privacySettings')}</h3>
              
              <div className="setting-item">
                <label>{t('profileVisibility')}</label>
                <select 
                  value={settings.privacy.profileVisibility}
                  onChange={(e) => handleSettingChange('privacy', 'profileVisibility', e.target.value)}
                >
                  <option value="public">{t('profilePublic')}</option>
                  <option value="private">{t('profilePrivate')}</option>
                  <option value="friends">{t('profileFriends')}</option>
                </select>
              </div>

              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.privacy.showEmail}
                    onChange={(e) => handleSettingChange('privacy', 'showEmail', e.target.checked)}
                  />
                  {t('showEmail')}
                </label>
                <p>{t('showEmailDesc')}</p>
              </div>

              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.privacy.showPhone}
                    onChange={(e) => handleSettingChange('privacy', 'showPhone', e.target.checked)}
                  />
                  {t('showPhone')}
                </label>
                <p>{t('showPhoneDesc')}</p>
              </div>

              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.privacy.allowMessages}
                    onChange={(e) => handleSettingChange('privacy', 'allowMessages', e.target.checked)}
                  />
                  {t('allowMessages')}
                </label>
                <p>{t('allowMessagesDesc')}</p>
              </div>
            </div>
          )}

          {activeTab === 'exam' && (
            <div className="settings-section">
              <h3>{t('examSettingsTitle')}</h3>
              
              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.exam.autoSave}
                    onChange={(e) => handleSettingChange('exam', 'autoSave', e.target.checked)}
                  />
                  {t('autoSave')}
                </label>
                <p>{t('autoSaveDesc')}</p>
              </div>

              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.exam.confirmBeforeSubmit}
                    onChange={(e) => handleSettingChange('exam', 'confirmBeforeSubmit', e.target.checked)}
                  />
                  {t('confirmBeforeSubmit')}
                </label>
                <p>{t('confirmBeforeSubmitDesc')}</p>
              </div>

              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.exam.showTimer}
                    onChange={(e) => handleSettingChange('exam', 'showTimer', e.target.checked)}
                  />
                  {t('showTimer')}
                </label>
                <p>{t('showTimerDesc')}</p>
              </div>

              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.exam.playSound}
                    onChange={(e) => handleSettingChange('exam', 'playSound', e.target.checked)}
                  />
                  {t('playSound')}
                </label>
                <p>{t('playSoundDesc')}</p>
              </div>

              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.exam.enableKeyboardShortcuts}
                    onChange={(e) => handleSettingChange('exam', 'enableKeyboardShortcuts', e.target.checked)}
                  />
                  {t('enableKeyboardShortcuts')}
                </label>
                <p>{t('enableKeyboardShortcutsDesc')}</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Settings;
