import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import Loading from '../components/Loading';
import './Schedule.css';

const Schedule = () => {
  const { user } = useAuth();
  const [exams, setExams] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');
  const [view, setView] = useState('list');

  useEffect(() => {
    fetchExams();
  }, []);

  const fetchExams = async () => {
    try {
      setLoading(true);
      let endpoint = '/exams';
      
      if (user.role === 'student') {
        endpoint = '/exams/student';
      } else if (user.role === 'teacher') {
        endpoint = '/exams/teacher';
      }
      
      const response = await api.get(endpoint);
      
      if (response.data.success) {
        setExams(response.data.data.exams || []);
      } else {
        setExams([]);
      }
    } catch (error) {
      console.error('Error fetching exams:', error);
      setExams([]);
    } finally {
      setLoading(false);
    }
  };

  const getExamStatus = (exam) => {
    if (!exam.schedule || !exam.schedule.startDate) return 'draft';
    
    const now = new Date();
    const startDate = new Date(exam.schedule.startDate);
    const endDate = exam.schedule.endDate ? new Date(exam.schedule.endDate) : startDate;
    
    if (now < startDate) return 'upcoming';
    if (now >= startDate && now <= endDate) return 'ongoing';
    return 'completed';
  };

  const getFilteredExams = () => {
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
    
    return exams.filter(exam => {
      if (!exam.schedule || !exam.schedule.startDate) return filter === 'all';
      
      const examDate = new Date(exam.schedule.startDate);
      const examDay = new Date(examDate.getFullYear(), examDate.getMonth(), examDate.getDate());
      
      switch (filter) {
        case 'upcoming':
          return examDay >= today;
        case 'today':
          return examDay.getTime() === today.getTime();
        case 'completed':
          return examDay < today;
        case 'ongoing':
          const examEnd = exam.schedule.endDate ? new Date(exam.schedule.endDate) : examDate;
          return examDate <= now && now <= examEnd;
        default:
          return true;
      }
    }).sort((a, b) => {
      if (!a.schedule?.startDate) return 1;
      if (!b.schedule?.startDate) return -1;
      return new Date(a.schedule.startDate) - new Date(b.schedule.startDate);
    });
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return 'Not scheduled';
    const date = new Date(dateString);
    return {
      date: date.toLocaleDateString('en-US', { 
        weekday: 'short', 
        year: 'numeric', 
        month: 'short', 
        day: 'numeric' 
      }),
      time: date.toLocaleTimeString('en-US', { 
        hour: '2-digit', 
        minute: '2-digit' 
      })
    };
  };

  const getTimeUntilExam = (examDate) => {
    const now = new Date();
    const exam = new Date(examDate);
    const diff = exam - now;
    
    if (diff <= 0) return null;
    
    const days = Math.floor(diff / (1000 * 60 * 60 * 24));
    const hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
    
    if (days > 0) return `${days}d ${hours}h`;
    if (hours > 0) return `${hours}h ${minutes}m`;
    return `${minutes}m`;
  };

  const filteredExams = getFilteredExams();

  const filterButtons = [
    { key: 'all', label: 'All Exams', icon: 'fas fa-list' },
    { key: 'upcoming', label: 'Upcoming', icon: 'fas fa-clock' },
    { key: 'today', label: 'Today', icon: 'fas fa-calendar-day' },
    { key: 'ongoing', label: 'Ongoing', icon: 'fas fa-play' },
    { key: 'completed', label: 'Completed', icon: 'fas fa-check' }
  ];

  if (loading) return <Loading />;

  return (
    <div className="schedule-container">
      <div className="schedule-header">
        <div className="header-content">
          <h1>Exam Schedule</h1>
          <p>View and manage your exam schedule</p>
        </div>
        
        <div className="header-actions">
          <div className="view-toggle">
            <button 
              className={`toggle-btn ${view === 'list' ? 'active' : ''}`}
              onClick={() => setView('list')}
            >
              <i className="fas fa-list"></i>
              List
            </button>
            <button 
              className={`toggle-btn ${view === 'calendar' ? 'active' : ''}`}
              onClick={() => setView('calendar')}
            >
              <i className="fas fa-calendar"></i>
              Calendar
            </button>
          </div>
        </div>
      </div>

      <div className="schedule-filters">
        <div className="filter-buttons">
          {filterButtons.map((filterOption) => {
            const examCount = exams.filter(exam => {
              if (filterOption.key === 'all') return true;
              if (filterOption.key === 'today') {
                return exam.schedule && 
                       new Date(exam.schedule.startDate).toDateString() === new Date().toDateString();
              }
              return getExamStatus(exam) === filterOption.key;
            }).length;

            return (
              <button
                key={filterOption.key}
                className={`filter-btn ${filter === filterOption.key ? 'active' : ''}`}
                onClick={() => setFilter(filterOption.key)}
              >
                <i className={filterOption.icon}></i>
                {filterOption.label}
                <span className="count">{examCount}</span>
              </button>
            );
          })}
        </div>
      </div>

      <div className="schedule-content">
        <div className="schedule-list">
          {filteredExams.length === 0 ? (
            <div className="no-exams">
              <i className="fas fa-calendar-times"></i>
              <h3>No exams found</h3>
              <p>There are no exams matching your current filter.</p>
              {user.role === 'teacher' && (
                <Link to="/create-exam" className="btn btn-primary">
                  <i className="fas fa-plus"></i>
                  Create New Exam
                </Link>
              )}
            </div>
          ) : (
            <div className="exams-grid">
              {filteredExams.map(exam => {
                const status = getExamStatus(exam);
                const dateTime = formatDateTime(exam.schedule?.startDate);
                const timeUntil = getTimeUntilExam(exam.schedule?.startDate);
                
                return (
                  <div key={exam._id} className={`exam-card ${status}`}>
                    <div className="exam-header">
                      <div className="exam-title">
                        <h3>{exam.title}</h3>
                        <span className={`status-badge ${status}`}>
                          {status.charAt(0).toUpperCase() + status.slice(1)}
                        </span>
                      </div>
                      {timeUntil && (
                        <div className="time-until">
                          <i className="fas fa-hourglass-half"></i>
                          {timeUntil}
                        </div>
                      )}
                    </div>

                    <div className="exam-details">
                      <div className="detail-row">
                        <i className="fas fa-book"></i>
                        <span>Subject: {exam.subject || 'General'}</span>
                      </div>
                      <div className="detail-row">
                        <i className="fas fa-calendar"></i>
                        <span>{dateTime.date}</span>
                      </div>
                      <div className="detail-row">
                        <i className="fas fa-clock"></i>
                        <span>{dateTime.time}</span>
                      </div>
                      <div className="detail-row">
                        <i className="fas fa-stopwatch"></i>
                        <span>Duration: {exam.duration || 60} minutes</span>
                      </div>
                      <div className="detail-row">
                        <i className="fas fa-question-circle"></i>
                        <span>Questions: {exam.questions?.length || 0}</span>
                      </div>
                      {exam.maxMarks && (
                        <div className="detail-row">
                          <i className="fas fa-trophy"></i>
                          <span>Max Marks: {exam.maxMarks}</span>
                        </div>
                      )}
                    </div>

                    <div className="exam-actions">
                      {user.role === 'student' && status === 'upcoming' && (
                        <Link to={`/exam/${exam._id}`} className="btn btn-primary">
                          <i className="fas fa-play"></i>
                          Take Exam
                        </Link>
                      )}
                      {user.role === 'student' && status === 'ongoing' && (
                        <Link to={`/exam/${exam._id}`} className="btn btn-success">
                          <i className="fas fa-play"></i>
                          Continue Exam
                        </Link>
                      )}
                      {user.role === 'student' && status === 'completed' && (
                        <Link to={`/exam/result/${exam._id}`} className="btn btn-secondary">
                          <i className="fas fa-chart-bar"></i>
                          View Result
                        </Link>
                      )}
                      {user.role === 'teacher' && (
                        <>
                          <Link to={`/exam/edit/${exam._id}`} className="btn btn-outline">
                            <i className="fas fa-edit"></i>
                            Edit
                          </Link>
                          <Link to={`/exam/results/${exam._id}`} className="btn btn-secondary">
                            <i className="fas fa-chart-bar"></i>
                            Results
                          </Link>
                        </>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Schedule;
