import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import Loading from '../components/Loading';
import './ExamList.css';

const ExamList = () => {
  const { user } = useAuth();
  const [exams, setExams] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    subject: 'all',
    status: 'all',
    sortBy: 'date',
    sortOrder: 'asc'
  });

  useEffect(() => {
    fetchExams();
  }, []);

  const fetchExams = async () => {
    try {
      setLoading(true);
      let endpoint = '/exams';
      
      if (user.role === 'student') {
        endpoint = '/exams/available';
      } else if (user.role === 'teacher') {
        endpoint = '/exams/my-exams';
      }
      
      const response = await api.get(endpoint);
      
      if (response.data.success) {
        setExams(response.data.data.exams || []);
      } else {
        setExams([]);
      }
    } catch (error) {
      console.error('Error fetching exams:', error);
      setExams([]);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  const getExamStatus = (exam) => {
    if (!exam.schedule || !exam.schedule.startDate) return 'draft';
    
    const now = new Date();
    const startDate = new Date(exam.schedule.startDate);
    const endDate = exam.schedule.endDate ? new Date(exam.schedule.endDate) : startDate;
    
    if (now < startDate) return 'upcoming';
    if (now >= startDate && now <= endDate) return 'ongoing';
    return 'completed';
  };

  const getFilteredExams = () => {
    let filtered = exams.filter(exam => {
      // Search filter
      const searchMatch = exam.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (exam.subject && exam.subject.toLowerCase().includes(searchTerm.toLowerCase())) ||
                         (exam.description && exam.description.toLowerCase().includes(searchTerm.toLowerCase()));
      
      if (!searchMatch) return false;
      
      // Subject filter
      if (filters.subject !== 'all' && exam.subject !== filters.subject) return false;
      
      // Status filter
      if (filters.status !== 'all') {
        const status = getExamStatus(exam);
        if (status !== filters.status) return false;
      }
      
      return true;
    });

    // Sort exams
    filtered.sort((a, b) => {
      let aValue, bValue;
      
      switch (filters.sortBy) {
        case 'title':
          aValue = a.title.toLowerCase();
          bValue = b.title.toLowerCase();
          break;
        case 'subject':
          aValue = (a.subject || '').toLowerCase();
          bValue = (b.subject || '').toLowerCase();
          break;
        case 'date':
          aValue = a.schedule?.startDate ? new Date(a.schedule.startDate) : new Date(0);
          bValue = b.schedule?.startDate ? new Date(b.schedule.startDate) : new Date(0);
          break;
        case 'duration':
          aValue = a.duration || 0;
          bValue = b.duration || 0;
          break;
        default:
          return 0;
      }
      
      if (filters.sortOrder === 'asc') {
        return aValue < bValue ? -1 : aValue > bValue ? 1 : 0;
      } else {
        return aValue > bValue ? -1 : aValue < bValue ? 1 : 0;
      }
    });

    return filtered;
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return 'Not scheduled';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      year: 'numeric',
      month: 'short', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const filteredExams = getFilteredExams();
  const subjects = [...new Set(exams.map(exam => exam.subject).filter(Boolean))];

  if (loading) return <Loading />;

  return (
    <div className="exam-list-container">
      <div className="exam-list-header">
        <div className="header-content">
          <h1>
            {user.role === 'student' ? 'Available Exams' : 
             user.role === 'teacher' ? 'My Exams' : 'All Exams'}
          </h1>
          <p>
            {user.role === 'student' ? 'Browse and take available exams' :
             user.role === 'teacher' ? 'Manage your created exams' : 'System-wide exam management'}
          </p>
        </div>
        
        {user.role === 'teacher' && (
          <Link to="/create-exam" className="btn btn-primary">
            <i className="fas fa-plus"></i>
            Create New Exam
          </Link>
        )}
      </div>

      <div className="exam-controls">
        <div className="search-section">
          <div className="search-box">
            <i className="fas fa-search"></i>
            <input
              type="text"
              placeholder="Search exams by title, subject, or description..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>

        <div className="filters-section">
          <select
            value={filters.subject}
            onChange={(e) => handleFilterChange('subject', e.target.value)}
            className="filter-select"
          >
            <option value="all">All Subjects</option>
            {subjects.map(subject => (
              <option key={subject} value={subject}>{subject}</option>
            ))}
          </select>

          <select
            value={filters.status}
            onChange={(e) => handleFilterChange('status', e.target.value)}
            className="filter-select"
          >
            <option value="all">All Status</option>
            <option value="upcoming">Upcoming</option>
            <option value="ongoing">Ongoing</option>
            <option value="completed">Completed</option>
            <option value="draft">Draft</option>
          </select>

          <select
            value={filters.sortBy}
            onChange={(e) => handleFilterChange('sortBy', e.target.value)}
            className="filter-select"
          >
            <option value="date">Sort by Date</option>
            <option value="title">Sort by Title</option>
            <option value="subject">Sort by Subject</option>
            <option value="duration">Sort by Duration</option>
          </select>

          <button
            className="sort-order-btn"
            onClick={() => handleFilterChange('sortOrder', filters.sortOrder === 'asc' ? 'desc' : 'asc')}
          >
            <i className={`fas fa-sort-${filters.sortOrder === 'asc' ? 'up' : 'down'}`}></i>
          </button>
        </div>
      </div>

      <div className="exam-stats">
        <div className="stat-card">
          <i className="fas fa-clipboard-list"></i>
          <div>
            <h3>{exams.length}</h3>
            <p>Total Exams</p>
          </div>
        </div>
        <div className="stat-card">
          <i className="fas fa-clock"></i>
          <div>
            <h3>{exams.filter(exam => getExamStatus(exam) === 'upcoming').length}</h3>
            <p>Upcoming</p>
          </div>
        </div>
        <div className="stat-card">
          <i className="fas fa-play"></i>
          <div>
            <h3>{exams.filter(exam => getExamStatus(exam) === 'ongoing').length}</h3>
            <p>Ongoing</p>
          </div>
        </div>
        <div className="stat-card">
          <i className="fas fa-filter"></i>
          <div>
            <h3>{filteredExams.length}</h3>
            <p>Filtered Results</p>
          </div>
        </div>
      </div>

      <div className="exam-list">
        {filteredExams.length === 0 ? (
          <div className="no-exams">
            <i className="fas fa-clipboard-list"></i>
            <h3>No exams found</h3>
            <p>Try adjusting your search criteria or filters</p>
            {user.role === 'teacher' && (
              <Link to="/create-exam" className="btn btn-primary">
                <i className="fas fa-plus"></i>
                Create Your First Exam
              </Link>
            )}
          </div>
        ) : (
          <div className="exams-grid">
            {filteredExams.map(exam => {
              const status = getExamStatus(exam);
              const startDate = formatDateTime(exam.schedule?.startDate);
              
              return (
                <div key={exam._id} className={`exam-card ${status}`}>
                  <div className="exam-header">
                    <div className="exam-title">
                      <h3>{exam.title}</h3>
                      <span className={`status-badge ${status}`}>
                        {status.charAt(0).toUpperCase() + status.slice(1)}
                      </span>
                    </div>
                  </div>

                  <div className="exam-meta">
                    <div className="meta-row">
                      <i className="fas fa-book"></i>
                      <span>{exam.subject || 'General'}</span>
                    </div>
                    <div className="meta-row">
                      <i className="fas fa-calendar"></i>
                      <span>{startDate}</span>
                    </div>
                    <div className="meta-row">
                      <i className="fas fa-clock"></i>
                      <span>{exam.duration || 60} minutes</span>
                    </div>
                    <div className="meta-row">
                      <i className="fas fa-question-circle"></i>
                      <span>{exam.questions?.length || 0} questions</span>
                    </div>
                    {exam.maxMarks && (
                      <div className="meta-row">
                        <i className="fas fa-trophy"></i>
                        <span>{exam.maxMarks} marks</span>
                      </div>
                    )}
                  </div>

                  {exam.description && (
                    <div className="exam-description">
                      <p>{exam.description}</p>
                    </div>
                  )}

                  <div className="exam-actions">
                    {user.role === 'student' && (
                      <>
                        {status === 'upcoming' && (
                          <Link to={`/exam/${exam._id}`} className="btn btn-primary">
                            <i className="fas fa-play"></i>
                            Take Exam
                          </Link>
                        )}
                        {status === 'ongoing' && (
                          <Link to={`/exam/${exam._id}`} className="btn btn-success">
                            <i className="fas fa-play"></i>
                            Continue
                          </Link>
                        )}
                        {status === 'completed' && (
                          <Link to={`/exam/result/${exam._id}`} className="btn btn-secondary">
                            <i className="fas fa-chart-bar"></i>
                            View Result
                          </Link>
                        )}
                      </>
                    )}
                    
                    {user.role === 'teacher' && (
                      <>
                        <Link to={`/exam/edit/${exam._id}`} className="btn btn-outline">
                          <i className="fas fa-edit"></i>
                          Edit
                        </Link>
                        <Link to={`/exam/results/${exam._id}`} className="btn btn-secondary">
                          <i className="fas fa-chart-bar"></i>
                          Results
                        </Link>
                        {status === 'draft' && (
                          <button className="btn btn-success">
                            <i className="fas fa-play"></i>
                            Publish
                          </button>
                        )}
                      </>
                    )}
                    
                    <Link to={`/exam/details/${exam._id}`} className="btn btn-info">
                      <i className="fas fa-info-circle"></i>
                      Details
                    </Link>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default ExamList;
