import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import Loading from '../components/Loading';
import './CreateExam.css';

const CreateExam = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);
  const [examData, setExamData] = useState({
    title: '',
    subject: '',
    description: '',
    duration: 60,
    startTime: '',
    endTime: '',
    maxScore: 100,
    passingScore: 60,
    instructions: '',
    shuffleQuestions: false,
    showResultsImmediately: true,
    allowRetake: false,
    securitySettings: {
      preventTabSwitching: true,
      requireFullscreen: true,
      disableRightClick: true,
      preventCopyPaste: true,
      enableProctoring: false
    }
  });

  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState({
    type: 'multiple-choice',
    question: '',
    options: ['', '', '', ''],
    correctAnswer: '',
    points: 1,
    explanation: '',
    image: null
  });

  const handleExamDataChange = (field, value) => {
    setExamData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSecuritySettingChange = (field, value) => {
    setExamData(prev => ({
      ...prev,
      securitySettings: {
        ...prev.securitySettings,
        [field]: value
      }
    }));
  };

  const handleQuestionChange = (field, value) => {
    setCurrentQuestion(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleOptionChange = (index, value) => {
    const newOptions = [...currentQuestion.options];
    newOptions[index] = value;
    setCurrentQuestion(prev => ({
      ...prev,
      options: newOptions
    }));
  };

  const addOption = () => {
    setCurrentQuestion(prev => ({
      ...prev,
      options: [...prev.options, '']
    }));
  };

  const removeOption = (index) => {
    if (currentQuestion.options.length > 2) {
      const newOptions = currentQuestion.options.filter((_, i) => i !== index);
      setCurrentQuestion(prev => ({
        ...prev,
        options: newOptions
      }));
    }
  };

  const validateQuestion = () => {
    if (!currentQuestion.question.trim()) {
      alert('Question text is required');
      return false;
    }

    if (currentQuestion.type === 'multiple-choice') {
      if (currentQuestion.options.some(opt => !opt.trim())) {
        alert('All options must be filled');
        return false;
      }
      if (!currentQuestion.correctAnswer) {
        alert('Please select the correct answer');
        return false;
      }
    }

    if (currentQuestion.type === 'true-false') {
      if (!currentQuestion.correctAnswer) {
        alert('Please select the correct answer');
        return false;
      }
    }

    if (currentQuestion.points <= 0) {
      alert('Points must be greater than 0');
      return false;
    }

    return true;
  };

  const addQuestion = () => {
    if (!validateQuestion()) return;

    const newQuestion = {
      ...currentQuestion,
      id: Date.now() // temporary ID
    };

    setQuestions(prev => [...prev, newQuestion]);
    
    // Reset current question
    setCurrentQuestion({
      type: 'multiple-choice',
      question: '',
      options: ['', '', '', ''],
      correctAnswer: '',
      points: 1,
      explanation: '',
      image: null
    });
  };

  const editQuestion = (index) => {
    setCurrentQuestion(questions[index]);
    setQuestions(prev => prev.filter((_, i) => i !== index));
  };

  const deleteQuestion = (index) => {
    if (window.confirm('Are you sure you want to delete this question?')) {
      setQuestions(prev => prev.filter((_, i) => i !== index));
    }
  };

  const validateExamData = () => {
    if (!examData.title.trim()) {
      alert('Exam title is required');
      return false;
    }
    if (!examData.subject.trim()) {
      alert('Subject is required');
      return false;
    }
    if (!examData.startTime) {
      alert('Start time is required');
      return false;
    }
    if (!examData.endTime) {
      alert('End time is required');
      return false;
    }
    if (new Date(examData.startTime) >= new Date(examData.endTime)) {
      alert('End time must be after start time');
      return false;
    }
    if (examData.duration <= 0) {
      alert('Duration must be greater than 0');
      return false;
    }
    return true;
  };

  const handleSubmit = async () => {
    if (!validateExamData()) return;
    
    if (questions.length === 0) {
      alert('Please add at least one question');
      return;
    }

    setLoading(true);
    
    try {
      const examPayload = {
        ...examData,
        questions: questions.map(q => ({
          ...q,
          id: undefined // Remove temporary ID
        })),
        createdBy: user._id
      };

      const response = await api.post('/exams', examPayload);
      
      alert('Exam created successfully!');
      navigate(`/exam/${response.data.exam._id}`);
    } catch (error) {
      console.error('Error creating exam:', error);
      alert('Error creating exam. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const nextStep = () => {
    if (currentStep === 1 && !validateExamData()) return;
    setCurrentStep(prev => prev + 1);
  };

  const prevStep = () => {
    setCurrentStep(prev => prev - 1);
  };

  const getTotalPoints = () => {
    return questions.reduce((total, question) => total + question.points, 0);
  };

  if (loading) return <Loading message="Creating exam..." overlay />;

  return (
    <div className="create-exam-container">
      <div className="create-exam-header">
        <h1>Create New Exam</h1>
        <div className="step-indicator">
          <div className={`step ${currentStep >= 1 ? 'active' : ''}`}>
            <span>1</span>
            <label>Exam Details</label>
          </div>
          <div className={`step ${currentStep >= 2 ? 'active' : ''}`}>
            <span>2</span>
            <label>Questions</label>
          </div>
          <div className={`step ${currentStep >= 3 ? 'active' : ''}`}>
            <span>3</span>
            <label>Review</label>
          </div>
        </div>
      </div>

      <div className="create-exam-content">
        {currentStep === 1 && (
          <div className="step-content">
            <div className="form-section">
              <h2>Basic Information</h2>
              <div className="form-grid">
                <div className="form-group">
                  <label>Exam Title *</label>
                  <input
                    type="text"
                    value={examData.title}
                    onChange={(e) => handleExamDataChange('title', e.target.value)}
                    placeholder="Enter exam title"
                  />
                </div>
                
                <div className="form-group">
                  <label>Subject *</label>
                  <input
                    type="text"
                    value={examData.subject}
                    onChange={(e) => handleExamDataChange('subject', e.target.value)}
                    placeholder="Enter subject"
                  />
                </div>
                
                <div className="form-group">
                  <label>Duration (minutes) *</label>
                  <input
                    type="number"
                    value={examData.duration}
                    onChange={(e) => handleExamDataChange('duration', parseInt(e.target.value))}
                    min="1"
                  />
                </div>
                
                <div className="form-group">
                  <label>Max Score *</label>
                  <input
                    type="number"
                    value={examData.maxScore}
                    onChange={(e) => handleExamDataChange('maxScore', parseInt(e.target.value))}
                    min="1"
                  />
                </div>
                
                <div className="form-group">
                  <label>Start Time *</label>
                  <input
                    type="datetime-local"
                    value={examData.startTime}
                    onChange={(e) => handleExamDataChange('startTime', e.target.value)}
                  />
                </div>
                
                <div className="form-group">
                  <label>End Time *</label>
                  <input
                    type="datetime-local"
                    value={examData.endTime}
                    onChange={(e) => handleExamDataChange('endTime', e.target.value)}
                  />
                </div>
              </div>
              
              <div className="form-group">
                <label>Description</label>
                <textarea
                  value={examData.description}
                  onChange={(e) => handleExamDataChange('description', e.target.value)}
                  placeholder="Enter exam description"
                  rows="4"
                />
              </div>
              
              <div className="form-group">
                <label>Instructions</label>
                <textarea
                  value={examData.instructions}
                  onChange={(e) => handleExamDataChange('instructions', e.target.value)}
                  placeholder="Enter exam instructions for students"
                  rows="6"
                />
              </div>
            </div>

            <div className="form-section">
              <h2>Exam Settings</h2>
              <div className="settings-grid">
                <div className="setting-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={examData.shuffleQuestions}
                      onChange={(e) => handleExamDataChange('shuffleQuestions', e.target.checked)}
                    />
                    Shuffle Questions
                  </label>
                </div>
                
                <div className="setting-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={examData.showResultsImmediately}
                      onChange={(e) => handleExamDataChange('showResultsImmediately', e.target.checked)}
                    />
                    Show Results Immediately
                  </label>
                </div>
                
                <div className="setting-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={examData.allowRetake}
                      onChange={(e) => handleExamDataChange('allowRetake', e.target.checked)}
                    />
                    Allow Retake
                  </label>
                </div>
              </div>
            </div>

            <div className="form-section">
              <h2>Security Settings</h2>
              <div className="settings-grid">
                <div className="setting-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={examData.securitySettings.preventTabSwitching}
                      onChange={(e) => handleSecuritySettingChange('preventTabSwitching', e.target.checked)}
                    />
                    Prevent Tab Switching
                  </label>
                </div>
                
                <div className="setting-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={examData.securitySettings.requireFullscreen}
                      onChange={(e) => handleSecuritySettingChange('requireFullscreen', e.target.checked)}
                    />
                    Require Fullscreen
                  </label>
                </div>
                
                <div className="setting-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={examData.securitySettings.disableRightClick}
                      onChange={(e) => handleSecuritySettingChange('disableRightClick', e.target.checked)}
                    />
                    Disable Right Click
                  </label>
                </div>
                
                <div className="setting-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={examData.securitySettings.preventCopyPaste}
                      onChange={(e) => handleSecuritySettingChange('preventCopyPaste', e.target.checked)}
                    />
                    Prevent Copy/Paste
                  </label>
                </div>
                
                <div className="setting-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={examData.securitySettings.enableProctoring}
                      onChange={(e) => handleSecuritySettingChange('enableProctoring', e.target.checked)}
                    />
                    Enable Proctoring
                  </label>
                </div>
              </div>
            </div>
          </div>
        )}

        {currentStep === 2 && (
          <div className="step-content">
            <div className="questions-section">
              <div className="questions-header">
                <h2>Add Questions</h2>
                <div className="questions-stats">
                  <span>Total Questions: {questions.length}</span>
                  <span>Total Points: {getTotalPoints()}</span>
                </div>
              </div>

              <div className="question-form">
                <div className="form-group">
                  <label>Question Type</label>
                  <select
                    value={currentQuestion.type}
                    onChange={(e) => handleQuestionChange('type', e.target.value)}
                  >
                    <option value="multiple-choice">Multiple Choice</option>
                    <option value="true-false">True/False</option>
                    <option value="essay">Essay</option>
                  </select>
                </div>

                <div className="form-group">
                  <label>Question Text *</label>
                  <textarea
                    value={currentQuestion.question}
                    onChange={(e) => handleQuestionChange('question', e.target.value)}
                    placeholder="Enter your question"
                    rows="4"
                  />
                </div>

                {currentQuestion.type === 'multiple-choice' && (
                  <div className="options-section">
                    <label>Options *</label>
                    {currentQuestion.options.map((option, index) => (
                      <div key={index} className="option-input">
                        <input
                          type="text"
                          value={option}
                          onChange={(e) => handleOptionChange(index, e.target.value)}
                          placeholder={`Option ${index + 1}`}
                        />
                        <div className="option-actions">
                          <label className="correct-answer">
                            <input
                              type="radio"
                              name="correctAnswer"
                              value={option}
                              checked={currentQuestion.correctAnswer === option}
                              onChange={(e) => handleQuestionChange('correctAnswer', e.target.value)}
                            />
                            Correct
                          </label>
                          {currentQuestion.options.length > 2 && (
                            <button
                              type="button"
                              className="btn btn-danger btn-sm"
                              onClick={() => removeOption(index)}
                            >
                              Remove
                            </button>
                          )}
                        </div>
                      </div>
                    ))}
                    <button
                      type="button"
                      className="btn btn-outline btn-sm"
                      onClick={addOption}
                    >
                      Add Option
                    </button>
                  </div>
                )}

                {currentQuestion.type === 'true-false' && (
                  <div className="tf-options">
                    <label>Correct Answer *</label>
                    <div className="radio-group">
                      <label>
                        <input
                          type="radio"
                          name="tfAnswer"
                          value="true"
                          checked={currentQuestion.correctAnswer === 'true'}
                          onChange={(e) => handleQuestionChange('correctAnswer', e.target.value)}
                        />
                        True
                      </label>
                      <label>
                        <input
                          type="radio"
                          name="tfAnswer"
                          value="false"
                          checked={currentQuestion.correctAnswer === 'false'}
                          onChange={(e) => handleQuestionChange('correctAnswer', e.target.value)}
                        />
                        False
                      </label>
                    </div>
                  </div>
                )}

                <div className="form-group">
                  <label>Points *</label>
                  <input
                    type="number"
                    value={currentQuestion.points}
                    onChange={(e) => handleQuestionChange('points', parseInt(e.target.value))}
                    min="1"
                  />
                </div>

                <div className="form-group">
                  <label>Explanation (Optional)</label>
                  <textarea
                    value={currentQuestion.explanation}
                    onChange={(e) => handleQuestionChange('explanation', e.target.value)}
                    placeholder="Explain the correct answer"
                    rows="3"
                  />
                </div>

                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={addQuestion}
                >
                  Add Question
                </button>
              </div>

              {questions.length > 0 && (
                <div className="questions-list">
                  <h3>Added Questions</h3>
                  {questions.map((question, index) => (
                    <div key={index} className="question-item">
                      <div className="question-header">
                        <span className="question-number">Q{index + 1}</span>
                        <span className="question-type">{question.type}</span>
                        <span className="question-points">{question.points} pts</span>
                      </div>
                      <div className="question-preview">
                        <p>{question.question}</p>
                        {question.type === 'multiple-choice' && (
                          <div className="options-preview">
                            {question.options.map((option, optIndex) => (
                              <span 
                                key={optIndex}
                                className={`option ${option === question.correctAnswer ? 'correct' : ''}`}
                              >
                                {option}
                              </span>
                            ))}
                          </div>
                        )}
                      </div>
                      <div className="question-actions">
                        <button
                          className="btn btn-secondary btn-sm"
                          onClick={() => editQuestion(index)}
                        >
                          Edit
                        </button>
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => deleteQuestion(index)}
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}

        {currentStep === 3 && (
          <div className="step-content">
            <div className="review-section">
              <h2>Review Your Exam</h2>
              
              <div className="review-card">
                <h3>Exam Details</h3>
                <div className="detail-grid">
                  <div><strong>Title:</strong> {examData.title}</div>
                  <div><strong>Subject:</strong> {examData.subject}</div>
                  <div><strong>Duration:</strong> {examData.duration} minutes</div>
                  <div><strong>Max Score:</strong> {examData.maxScore} points</div>
                  <div><strong>Start Time:</strong> {new Date(examData.startTime).toLocaleString()}</div>
                  <div><strong>End Time:</strong> {new Date(examData.endTime).toLocaleString()}</div>
                </div>
              </div>

              <div className="review-card">
                <h3>Questions Summary</h3>
                <div className="questions-summary">
                  <p><strong>Total Questions:</strong> {questions.length}</p>
                  <p><strong>Total Points:</strong> {getTotalPoints()}</p>
                  <p><strong>Question Types:</strong></p>
                  <ul>
                    <li>Multiple Choice: {questions.filter(q => q.type === 'multiple-choice').length}</li>
                    <li>True/False: {questions.filter(q => q.type === 'true-false').length}</li>
                    <li>Essay: {questions.filter(q => q.type === 'essay').length}</li>
                  </ul>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      <div className="create-exam-footer">
        <div className="step-navigation">
          {currentStep > 1 && (
            <button className="btn btn-secondary" onClick={prevStep}>
              Previous
            </button>
          )}
          
          {currentStep < 3 ? (
            <button className="btn btn-primary" onClick={nextStep}>
              Next
            </button>
          ) : (
            <button className="btn btn-success" onClick={handleSubmit}>
              Create Exam
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default CreateExam;
